INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2078-2093-techno-legacy', 'Берлин 2078-2093 — Наследие техно', 'Игрок выполняет гильдейские задания, набирает Legacy-очки и фиксирует традиции для следующей эры.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2078-2093-techno-legacy", "title": "Берлин 2078-2093 — Наследие техно", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "legacy"], "topics": ["cultural_preservation", "guild_progression"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "liveops-service", "reputation-service", "social-service"], "related_documents": [{"id": "github-issue-702", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/702", "relation": "migrated_to", "migrated_at": "2025-11-23T00:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2078-2093/quest-042-techno-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гильдейской линии не хватало механики передачи техно-культуры между лигами.\\n", "goal": "Активировать Legacy-программу Berlin Techno Collective, обучить новое поколение и сохранить культурный архив.\\n", "essence": "Игрок выполняет гильдейские задания, набирает Legacy-очки и фиксирует традиции для следующей эры.\\n", "key_points": ["Коллектив требует кооперации между рейвами, обучением и документированием истории.", "Legacy-очки влияют на бонусы и стартовые условия будущих сезонов.", "Квест связывает прошлые легенды клубов с будущими поколениями."]}, "content": {"sections": [{"id": "enrollment", "title": "Присоединение к коллективу", "body": "Игрок проходит собеседование у кураторов Berghain и Tresor, получает доступ к внутренним поручениям и ресурсам.\\n", "mechanics_links": [], "assets": []}, {"id": "events", "title": "Организация рейвов", "body": "Нужно провести серию тематических вечеринок: Detroit tribute, queer-ночь, экспериментальные сеты. Успех повышает репутацию коллектива.\\n", "mechanics_links": [], "assets": []}, {"id": "mentorship", "title": "Обучение новых DJ", "body": "Игрок создаёт мастер-классы, раздаёт наборы треков и помогает стажёрам подготовиться к дебюту на сцене.\\n", "mechanics_links": [], "assets": []}, {"id": "archive", "title": "Сохранение истории", "body": "Создание цифрового музея: интервью ветеранов, оцифровка сетов, сохранение визуальной эстетики. Legacy-очки растут от полноты архива.\\n", "mechanics_links": [], "assets": []}, {"id": "handoff", "title": "Передача наследия", "body": "Игрок готовит манифест для следующей лиги, определяет бонусы (баффы на старт, специальные эмоции) и фиксирует решения в хрониках.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP, титул «Хранитель техно».\\n- Legacy-бонусы в следующем сезоне: повышенная репутация, доступ к эксклюзивным рейвам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 702, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Наследие техно» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

