INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vienna-classical-music', 'Вена — Классическая музыка', 'Игрок покупает билет на концерт Musikverein, переживает выступления Моцарта и Штрауса и получает культурные бонусы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vienna-classical-music", "title": "Вена — Классическая музыка", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T20:32:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T20:32:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["vienna", "music", "culture"], "topics": ["performing_arts", "live_events", "seasonal_content"], "related_systems": ["event-scheduler", "audio-system", "reputation-system"], "related_documents": [{"id": "canon-quest-vienna-coffee-culture", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/vienna/2020-2029/quest-002-classical-music.md", "visibility": "internal", "audience": ["narrative", "liveops", "audio"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Концерт в Musikverein был описан кратко и не указывал влияние на сезонные события и музыкальные системы.", "goal": "Сформировать квест, в котором игрок посещает легендарный зал и взаимодействует с музыкальными брендами города.", "essence": "Игрок покупает билет на концерт Musikverein, переживает выступления Моцарта и Штрауса и получает культурные бонусы.", "key_points": ["Включены подготовка, посещение зала и возможный новогодний концерт.", "Игрок может собирать аудио-фрагменты и улучшать репутацию у музыкальных фракций.", "Событие синхронизируется с глобальными трансляциями и сезонным контентом."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: event. Сложность: easy. Формат: solo. Длительность: 2–4 часа.\\nНаграды: 2 500 XP, -60 едди, +30 культуры.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Приобрести билет в Musikverein или Венскую оперу, учитывая расписание.\\n2. Исследовать зал с виртуальным гидом и акустическими тестами.\\n3. Послушать программу из произведений Моцарта, Штрауса и Бетховена, собирая аудио-объекты.\\n4. При желании посетить новогодний концерт с глобальной трансляцией и уникальными наградами.\\n5. Завершить посещение, получив отзывы критиков и культурные очки.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "event-scheduler активирует специальные концерты и сезонные линейки.\\naudio-system сохраняет живые исполнения в коллекции игрока.\\nreputation-system повышает статус у музыкальных фракций и клубов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Описан золотой зал Musikverein, традиции новогоднего концерта и история композиторов Вены.\\nОтмечена роль города как «музыкальной столицы мира».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает ачивку «Меломан Вены» и доступ к эксклюзивным музыкальным сетлистам.\\nУчастие в новогоднем событии даёт редкие эмоуты и декоративные предметы для жилья.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в YAML 1.1.0, добавлены сезонные крючки и музыкальные награды."}, {"version": "0.1.0", "date": "2025-11-03", "author": "narrative_team", "changes": "Markdown-описание концерта Musikverein."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

