INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vienna-2029-009-ball-season', 'Вена: сезон балов', 'Игрок готовится к балу, проходит обучение, вступает в ночную культуру Вены и повышает статус.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vienna-2029-009-ball-season", "title": "Вена: сезон балов", "document_type": "canon", "category": "city-quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T06:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vienna", "ballroom", "culture"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/vienna/2020-2029/quest-009-vienna-ball-season.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не описан структурированный опыт участия в венском сезоне балов и необходимых подготовках.", "goal": "Задать этапы вступления в Opernball, обучение вальсу и социальные последствия выбора.", "essence": "Игрок готовится к балу, проходит обучение, вступает в ночную культуру Вены и повышает статус.", "key_points": ["450 балов в зимний сезон и обязательный вальс.", "Выбор между членством в Opernball и альтернативными балами.", "Социальные бонусы и расходы на атрибуты роскоши."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `VIENNA-2029-009`\\n- Сезон: январь–март, продолжительность события до 8 часов.\\n- Локации: Wiener Staatsoper, Hofburg и частные балы.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "preparation", "title": "Подготовка", "body": "1. Получить приглашение на Opernball или другой бал (социальные проверки).\\n2. Рассчитаться за аренду смокинга/платья и провести репетиции вальса.\\n3. Изучить этикет: midnight quadrille, приветствие хозяев, запреты на запись.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "event_flow", "title": "Ночь бала", "body": "- Участие в церемониальном открытии и танце дебютантов.\\n- Midnight quadrille и серия вальсов до рассвета.\\n- Опции решения: остаться до конца с элитой или уйти к уличным праздникам.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3000 XP, расходы 200 евро на подготовку.\\n- Навык «Вальс» +25 и социальный рейтинг среди венских элит.\\n- Ачивка «Король бала». Негативный исход при провале этикета снижает репутацию.\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста «Сезон балов» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

