INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2078-2093-final-rave', 'Берлин 2078-2093 — «Финальный рейв»', 'Игрок присоединяется к синхронизированному городскому рейву, совершает клубный маршрут и встречает рассвет под общим треком.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2078-2093-final-rave", "title": "Берлин 2078-2093 — «Финальный рейв»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "marathon"], "topics": ["endurance_event", "nightlife_legacy"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "liveops-service", "wellbeing-service", "social-service"], "related_documents": [{"id": "github-issue-703", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/703", "relation": "migrated_to", "migrated_at": "2025-11-23T00:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2078-2093/quest-043-final-rave.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финалу техно-эры Берлина не хватало масштабного события, объединяющего все клубы и подчёркивающего наследие лиги.", "goal": "Провести 72-часовой марафон-рейв, охватывающий ключевые площадки города и проверяющий выносливость участников.", "essence": "Игрок присоединяется к синхронизированному городскому рейву, совершает клубный маршрут и встречает рассвет под общим треком.", "key_points": ["Все культовые клубы работают синхронно, создавая единый аудиопоток.", "Механики выносливости и управления ресурсами поддерживают участие 72 часа.", "Финал фиксирует историю техно-лиги и открывает путь к перезапуску."]}, "content": {"sections": [{"id": "kickoff", "title": "Старт пятничного марафона", "body": "Игрок получает электронный браслет, синхронизирует расписание и начинает рейв-путешествие с открывающего сета на Alexanderplatz.\\n", "mechanics_links": [], "assets": []}, {"id": "club_crawl", "title": "Клаб-краул по Берлину", "body": "В течение первых суток маршрут охватывает Berghain, Watergate и Sisyphos. Нужно поддерживать темп, пополнять ресурсы и собирать\\nподписи диджеев.\\n", "mechanics_links": [], "assets": []}, {"id": "endurance", "title": "Испытание выносливости", "body": "На вторые сутки рейв переходит в Tresor и подпольные площадки. Игрок следит за уровнем энергии, стрессом и взаимодействует с волонтёрами.\\n", "mechanics_links": [], "assets": []}, {"id": "dawn", "title": "Рассвет финального дня", "body": "Заключительные часы проходят на Tempelhofer Feld: общий сет «Never stop dancing», коллективный обратный отсчёт и фиксация в хрониках.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 10 000 XP, выносливость +15, уникальный бафф «72-Hour Legend».\\n- Доступ к документальному архиву техно-лиги и титул «Рейв-Марафонец».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 703, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Финальный рейв» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

