INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2078-2093-goodbye-berlin', 'Берлин 2078-2093 — «Прощай, Берлин»', 'Игрок проходит серию точек от East Side Gallery до Рейхстага, фиксируя истории и закрывая долгосрочные связи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2078-2093-goodbye-berlin", "title": "Берлин 2078-2093 — «Прощай, Берлин»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "farewell", "city_tour"], "topics": ["emotional_journey", "cultural_route"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "social-service", "liveops-service"], "related_documents": [{"id": "github-issue-705", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/705", "relation": "migrated_to", "migrated_at": "2025-11-23T00:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2078-2093/quest-045-goodbye-berlin.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финалу городской арки требуется эмоциональный маршрут по ключевым локациям Берлина.", "goal": "Дать игроку прощальный тур с техно-саундтреком и персональными воспоминаниями.", "essence": "Игрок проходит серию точек от East Side Gallery до Рейхстага, фиксируя истории и закрывая долгосрочные связи.", "key_points": ["Каждый чекпоинт активирует персональные флэшбеки и бонусные диалоги.", "Саундтрек синхронизируется с маршрутом, подчеркивая настроение сцен.", "Финал открывает возможность вернуться после перезапуска в роли гостя."]}, "content": {"sections": [{"id": "east_side_gallery", "title": "Утро у East Side Gallery", "body": "Игрок активирует аудиогид, изучает граффити и сохраняет первые воспоминания о стене.\\n", "mechanics_links": [], "assets": []}, {"id": "museum_island", "title": "Культурный полдень", "body": "На острове музеев нужно завершить коллекцию экспонатов и записать финальный дневник об истории города.\\n", "mechanics_links": [], "assets": []}, {"id": "berghain_evening", "title": "Вечер в Berghain", "body": "Последний сет в легендарном клубе. Игрок выбирает трек прощания и прощает старые конфликты с фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "gate_night", "title": "Ночь у Бранденбургских ворот", "body": "Коллективное пение гимна техно-лиги, запись прощального манифеста и запуск дронов памяти.\\n", "mechanics_links": [], "assets": []}, {"id": "reichstag_dawn", "title": "Рассвет на куполе Рейхстага", "body": "Игрок встречает новую эру, делает финальный снимок и добавляет «Danke, Berlin» в общественный архив.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP, бафф «Berliner Herz» и доступ к ностальгическим репризам.\\n- Открыт режим гостевых визитов после перезапуска.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 705, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Прощай, Берлин» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.