INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2078-2093-bear-symbol', 'Берлин 2078-2093 — «Символ медведя»', 'Игрок путешествует по городу, фотографирует статуи, активирует AR-истории и завершает встречей у Рейхстага.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2078-2093-bear-symbol", "title": "Берлин 2078-2093 — «Символ медведя»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:02:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "buddy_bears", "collection"], "topics": ["urban_symbols", "cultural_memory"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "social-service"], "related_documents": [{"id": "github-issue-706", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/706", "relation": "migrated_to", "migrated_at": "2025-11-23T00:02:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2078-2093/quest-046-bear-symbol.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Символическая линия Берлина нуждалась в квесте про «Berliner Bär» и коллекцию Buddy Bears перед финалом.", "goal": "Собрать цифровую галерею медведей, встретить последнюю живую маскоту и попрощаться с символом города.", "essence": "Игрок путешествует по городу, фотографирует статуи, активирует AR-истории и завершает встречей у Рейхстага.", "key_points": ["Каждое фото открывает историю художника и эпоху города.", "Финальный медведь транслирует послание горожанам и приглашает в новую эру.", "Коллекция превращается в интерактивную экспозицию для будущих поколений."]}, "content": {"sections": [{"id": "hunt_start", "title": "Старт охоты на медведей", "body": "Игрок получает holo-альбом и карту с отмеченными статуями, выбирает маршрут и настраивает AR-линзы.\\n", "mechanics_links": [], "assets": []}, {"id": "photo_challenge", "title": "Фото-миссия по городу", "body": "Нужно сделать 50 снимков Buddy Bears, фиксируя уникальные дизайны и короткие истории авторов.\\n", "mechanics_links": [], "assets": []}, {"id": "berlinale_stop", "title": "Пауза у Berlinale", "body": "Фотозона кинофестиваля открывает клип о Золотом медведе и награждает автографами режиссёров.\\n", "mechanics_links": [], "assets": []}, {"id": "final_bear", "title": "Встреча последнего медведя", "body": "У Рейхстага игрок встречает живую маскоту. Необходимо выбрать прощальную речь и подарить коллекцию городу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP, коллекция «Friend of the Bear» и доступ к AR-галерее Buddy Bears.\\n- Титул «Друг медведя» для социального профиля.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 706, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Символ медведя» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED