cs_links": [], "assets": []}, {"id": "rewards_mechanics", "title": "Награды и механики", "body": "XP: 3000, Валюта: 0, Репутация «Экономика»: +25. Игрок получает достижение «Нефтяной Магнат».\\nКлючевые механики — экономический анализ, корпоративные связи и фракционный выбор.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 342, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-houston-oil-capital"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Нефтяная столица» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

