INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2078-2093-unity-techno', 'Берлин 2078-2093 — «Техно единства»', 'Игрок ведёт колонну, организует остановки памяти и синхронизирует финальный трек «United by techno».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2078-2093-unity-techno", "title": "Берлин 2078-2093 — «Техно единства»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:02:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "unity"], "topics": ["remembrance_march", "cultural_unification"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "liveops-service", "diplomacy-service", "social-service"], "related_documents": [{"id": "github-issue-707", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/707", "relation": "migrated_to", "migrated_at": "2025-11-23T00:02:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2078-2093/quest-047-unity-techno.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальному параду вдоль Берлинской стены требовалось системное описание единства через музыку.", "goal": "Провести техно-шествие из East Side Gallery к Бранденбургским воротам, вспоминая прошлое и запуская будущее.", "essence": "Игрок ведёт колонну, организует остановки памяти и синхронизирует финальный трек «United by techno».", "key_points": ["Марш активирует интерактивные станции памяти вдоль бывшей стены.", "Решения игрока влияют на дипломатические отношения и настроения фракций.", "Финальный обратный отсчёт запускает новую фазу техно-лиги."]}, "content": {"sections": [{"id": "assembly", "title": "Сбор у East Side Gallery", "body": "Игрок активирует стартовый сет, формирует колонны и распределяет роли волонтёров и артистов.\\n", "mechanics_links": [], "assets": []}, {"id": "march_route", "title": "Парад вдоль стены", "body": "Колонна проходит 13 км маршрута. На каждой остановке нужно синхронизировать музыку, голограммы и исторические хроники.\\n", "mechanics_links": [], "assets": []}, {"id": "memory_nodes", "title": "Узлы памяти", "body": "Игрок активирует AR-инсталляции у Checkpoint Charlie и Potsdamer Platz, усиливая послание единства.\\n", "mechanics_links": [], "assets": []}, {"id": "finale", "title": "Финал у Бранденбургских ворот", "body": "Парад завершается финальным сетом и коллективным манифестом. Игрок выбирает ключевые тезисы и запускает новый сезон.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP, бафф «Techno Unity» и доступ к архиву «Einigkeit durch Musik».\\n- Титул «Техно-освободитель» и влияние на межрегиональные дипломатические миссии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 707, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Техно единства» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, ob