INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2093-eternal-techno', 'Берлин 2078-2093 — Вечное техно', 'Игрок выбирает наследие, танцует последний сет и наблюдает кат-сцену «Techno never dies».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2093-eternal-techno", "title": "Берлин 2078-2093 — Вечное техно", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T00:02:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "finale", "techno"], "topics": ["league_transition", "cultural_legacy"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "liveops-service", "social-service", "legend-service"], "related_documents": [{"id": "github-issue-710", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/710", "relation": "migrated_to", "migrated_at": "2025-11-23T00:02:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/berlin/2078-2093/quest-050-eternal-techno.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальному сезону Берлина требовался ритуал legacy и эмоциональное прощание с городом.", "goal": "Сформировать финальный танец в Berghain, закрепить legacy-набор и подготовить переход в новую лигу.", "essence": "Игрок выбирает наследие, танцует последний сет и наблюдает кат-сцену «Techno never dies».", "key_points": ["Встроенная система legacy-наград.", "Кат-сцена с голосом ведущего техно-комьюнити.", "Сводка всех 50 квестов завершённой арки."]}, "content": {"sections": [{"id": "legacy_choice", "title": "Legacy-выбор", "body": "Игрок просматривает достижения и выбирает, какие артефакты, титулы и моды перенести в новую лигу. Решение влияет на стартовые бонусы.\\n", "mechanics_links": ["mechanics/progression/legacy-selection.yaml"], "assets": []}, {"id": "entry", "title": "Последний вход в Berghain", "body": "Секвенция проверок: фейс-контроль, сканер эмоций, воспоминания о ключевых событиях. Фантомы друзей провожают героя внутрь.\\n", "mechanics_links": ["mechanics/exploration/final-entry.yaml"], "assets": []}, {"id": "dance", "title": "Финальный танец", "body": "Игрок синхронизирует движения с сетом DJ. Мини-игра на ритм и эмоции фиксирует индивидуальный стиль.\\n", "mechanics_links": ["mechanics/liveops/rhythm-dance.yaml"], "assets": []}, {"id": "cutscene", "title": "Кат-сцена «Techno never dies»", "body": "Видео-сцена показывает бесконечный танцпол, голос повествует о вечности техно и приглашает игрока в новую лигу.\\n", "mechanics_links": ["mechanics/media/cinematic-trigger.yaml"], "assets": []}, {"id": "epilogue", "title": "Послесловие", "body": "Игрок получает сводку из 50 квестов, благодарности от NPC и ссылку на архивные материалы. Активируется старт новой лиги.\\n", "mechanics_links": ["mechanics/progression/season-wrap.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Титул «Ветеран Берлина 2020-2093», перенос legacy-набора и доступ к стартовым бустам новой лиги.\\n- Эксклюзивная эмоция «Techno never dies» и приглашение на премьерный liveops.\\n", "mechanics_links": ["mechanics/progression/title-unlock.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 710, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Финальный квест «Вечное техно» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

