        NULL,
        '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb,
        '[{"id": "assess_damage", "text": "Оценить ущерб от пожара и составить план реставрации", "type": "interact", "target": "notre_dame_assessment", "count": 1, "optional": false}, {"id": "choose_restoration_style", "text": "Выбрать стиль реставрации: исторический, модерн или гибрид", "type": "interact", "target": "notre_dame_restoration_choice", "count": 1, "optional": false}, {"id": "restore_spire", "text": "Восстановить шпиль с использованием AR и противопожарных технологий", "type": "interact", "target": "notre_dame_spire_restoration", "count": 1, "optional": false}, {"id": "protect_cathedral", "text": "Установить системы защиты собора от будущих пожаров", "type": "interact", "target": "notre_dame_protection_systems", "count": 1, "optional": false}, {"id": "finalize_restoration", "text": "Завершить реставрацию и зафиксировать культурный рейтинг", "type": "interact", "target": "notre_dame_finalization", "count": 1, "optional": false}]'::jsonb,
