-- Quest: quest-london-2040-underground-network
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-underground-network', 'Подземная Сеть', 'Игрок исследует заброшенные станции, размещает оборудование и защищает сеть от рейда NetWatch, получая доступ к эксклюзивной инфраструктуре.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-underground-network", "title": "Подземная Сеть", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "hacking", "infrastructure"], "topics": ["post-net", "underground-networking"], "related_systems": ["netrunner-system", "metro-infrastructure"], "related_documents": [{"id": "quest-moscow-2040-network-rib", "relation": "parallels"}, {"id": "github-issue-670", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/670", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-022-underground-network.md", "visibility": "internal", "audience": ["lore", "narrative", "netrunner-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "После фрагментации глобального NET необходимо описать локальные сетевые инициативы в Лондоне.", "goal": "Создать квест о построении подпольной сети в туннелях метро, подчёркивающий роль нетраннеров.", "essence": "Игрок исследует заброшенные станции, размещает оборудование и защищает сеть от рейда NetWatch, получая доступ к эксклюзивной инфраструктуре.", "key_points": ["Использование исторических туннелей метро в качестве сетевых узлов.", "Выполнение технических задач и защита от сил порядка.", "Награда предоставляет постоянные бонусы и доступ к новой фракции."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Фрагментация NET вынудила лондонских нетраннеров искать автономные решения. Заброшенные ветки The Tube стали идеальной платформой для независимой сетевой сети, скрытой от NetWatch и корпораций.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Заброшенные станции Aldwych и Down Street превращены в серверные залы.\\n- Викторианские туннели обеспечивают естественное охлаждение оборудования.\\n- Сеть интегрируется с городскими легендами о призрачных поездах.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Получить карту устаревших туннелей метро.\\n2. Разместить серверные стойки на пяти ключевых станциях.\\n3. Проложить кабели и обеспечить питание.\\n4. Активировать сеть и провести тест связи.\\n5. Отразить рейд NetWatch, защищая инфраструктуру.\\n", "mechanics_links": ["mechanics/hacking/network-construction.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP.\\n- Валюта: 5 000 эдди.\\n- Постоянный доступ к подземной сети и бесплатный интернет в метро.\\n", "mechanics_links": ["mechanics/progression/resource-rewards.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест перекликается с московской линией «Сетевое Рёбро» и открывает дополнительные миссии для нетраннеров в Европе.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 670, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Подземная Сеть» перенесён в YAML и структурирован по шаблону."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

