-- Quest: quest-london-2040-stonehenge-ritual
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-stonehenge-ritual', 'Ритуал у Стоунхенджа', 'Игрок получает приглашение, участвует в ритуале солнцестояния и решает, как поступить после вмешательства ИИ.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-stonehenge-ritual", "title": "Ритуал у Стоунхенджа", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["stonehenge", "druids", "blackwall"], "topics": ["mystical_rituals", "ai_worship"], "related_systems": ["narrative-service", "ai-cult-system"], "related_documents": [{"id": "github-issue-671", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/671", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-023-stonehenge-ritual.md", "visibility": "internal", "audience": ["lore", "narrative", "event-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется описать смешение неоязыческих традиций и технологий через квест у Стоунхенджа.", "goal": "Разработать сценарий ритуала, где друиды пытаются связаться с ИИ за Blackwall и игроку предоставляется выбор реакции.", "essence": "Игрок получает приглашение, участвует в ритуале солнцестояния и решает, как поступить после вмешательства ИИ.", "key_points": ["Сочетание кельтских традиций и киберпанк-эстетики.", "Выбор между сотрудничеством с ИИ, изгнанием и передачей информации NetWatch.", "Уникальное благословение, влияющее на природные навыки."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Неоязыческие культы Британии ищут новую силу в эпоху технологий. Друиды Стоунхенджа адаптировали обряды, вплетая голограммы и Blackwall-ритуалы, и приглашают игрока на летнее солнцестояние.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Стоунхендж остаётся центральной сценой кельтских ритуалов.\\n- Современные друиды используют светодиодные одеяния и кибер-руны.\\n- Музыкальное сопровождение объединяет традиционные мотивы и электронные биты.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Получить приглашение от неоязыческого круга.\\n2. Добраться до Стоунхенджа и пройти подготовительный обряд.\\n3. Принять участие в пике ритуала во время солнцестояния.\\n4. Столкнуться с сигналом ИИ из-за Blackwall, вызывающим нарушение обряда.\\n5. Выбрать реакцию: установить контакт, изгнать сущность или вызвать NetWatch.\\n", "mechanics_links": ["mechanics/narrative/ritual-encounters.yaml"], "assets": []}, {"id": "outcomes", "title": "Исходы", "body": "- **Общение с ИИ:** игрок получает уникальные знания и доступ к ветке ИИ-культа.\\n- **Изгнание:** укрепляет доверие друидов и сохраняет традиции.\\n- **Призвать NetWatch:** усиливает защиту, но вызывает недовольство культа.\\n", "mechanics_links": ["mechanics/narrative/consequence-tracking.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP.\\n- Благословение друидов: +10 % к природным навыкам.\\n- Репутация: изменение отношений с культом и NetWatch.\\n", "mechanics_links": ["mechanics/progression/buff-system.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 671, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Ритуал у Стоунхенджа» перенесён в YAML и адаптирован к шаблону знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

