INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-thames-pirates', 'Пираты Темзы', 'Игрок знакомится с пиратами, проходит испытание на корпоратской барже и определяет свою роль внутри гильдии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-thames-pirates", "title": "Пираты Темзы", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "thames", "pirates"], "topics": ["water_combat", "faction_content"], "related_systems": ["naval-combat-system", "faction-service"], "related_documents": [{"id": "github-issue-672", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/672", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-024-thames-pirates.md", "visibility": "internal", "audience": ["lore", "narrative", "systems-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен сюжет о водной банде, контролирующей Темзу и речную торговлю.", "goal": "Зафиксировать квест о вступлении в пиратскую гильдию Темзы с акцентом на морские бои и выбор ролей.", "essence": "Игрок знакомится с пиратами, проходит испытание на корпоратской барже и определяет свою роль внутри гильдии.", "key_points": ["Активные водные столкновения и ограбления корпораций.", "Пиратский колорит с британскими традициями и жаргоном.", "Награда включает доступ к гильдии и персональный катер."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Затопленные доки Темзы стали домом водной банды, которая контролирует торговлю и логистику Лондона. Корпорации пытаются вернуть контроль, но пираты используют хитрость и мобильность на воде.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Морская история Британии вдохновляет пиратские традиции.\\n- Затопленные доки и баржи служат базами и точками ремонта.\\n- Пиратский кодекс адаптирован под киберпанк-реалии.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Встретиться с капитаном пиратов на плавучем баре.\\n2. Пройти испытание — ограбить корпоратскую баржу.\\n3. Участвовать в водном бою и отразить погоню охраны.\\n4. Вступить в пиратскую гильдию и пройти посвящение.\\n5. Выбрать роль: капитан, боцман или навигатор, влияющую на будущий геймплей.\\n", "mechanics_links": ["mechanics/combat/naval-encounters.yaml"], "assets": []}, {"id": "flavor", "title": "Атмосфера", "body": "Пиратский жаргон с британскими выражениями, романтика таверн на воде, ром и грог, и флаги Jolly Roger с неоновыми элементами создают фирменную атмосферу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP.\\n- Валюта: 10 000 эдди.\\n- Доступ к пиратской гильдии и персональный катер.\\n", "mechanics_links": ["mechanics/progression/faction-rewards.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает водные миссии Темзы, влияет на торговые маршруты и отношения с корпорациями, владеющими причалами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 672, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Пираты Темзы» перенесён в YAML и приведён к общей схеме знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

