-- Quest: quest-london-2040-abbey-road
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-abbey-road', 'Студия Abbey Road', 'Игрок с достаточной репутацией музыканта получает доступ к студии, создаёт трек, делает знаковое фото на переходе и получает доходы от релиза.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-abbey-road", "title": "Студия Abbey Road", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "music", "abbey-road"], "topics": ["cultural_content", "passive_income"], "related_systems": ["music-system", "economy-service", "fame-system"], "related_documents": [{"id": "github-issue-674", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/674", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-026-abbey-road.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно раскрыть музыкальную культуру Лондона и дать игрокам путь развития через студию Abbey Road.", "goal": "Описать квест о записи альбома с нейро-синтезатором и запуском пассивного дохода.", "essence": "Игрок с достаточной репутацией музыканта получает доступ к студии, создаёт трек, делает знаковое фото на переходе и получает доходы от релиза.", "key_points": ["Комбинация культурного наследия Beatles и футуристической технологии нейро-музыки.", "Мини-игра создания трека и пассивная экономика релизов.", "Рост славы и открытие музыкальной карьерной ветки."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Студия Abbey Road стала нейро-музыкальным центром, куда допускают только музыкантов с достаточной репутацией. Квест служит точкой входа в музыкальную карьеру и расширяет социальный контент Лондона.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Сохранённый пешеходный переход и граффити Beatles.\\n- NPC-музыканты, представляющие британские жанры от рока до электроники.\\n- Публика и фанаты, которые реагируют на новые релизы.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Получить доступ к студии при славе музыкантов уровня 5+.\\n2. Сформировать композицию через мини-игру нейро-синтезатора.\\n3. Записать альбом в одной из студий Abbey Road.\\n4. Сделать фото на переходе в духе Beatles и подготовить маркетинг.\\n5. Выпустить альбом и настроить пассивный доход от продаж.\\n", "mechanics_links": ["mechanics/career/music-production.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP.\\n- Пассивный доход 1 000–10 000 эдди в неделю в зависимости от продвижения.\\n- Рост славы на +10 и достижение «Crossing Abbey Road».\\n", "mechanics_links": ["mechanics/economy/passive-income.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает музыкальную карьерную линию, интегрируется с системами славы, концертов и социальных мероприятий Лондона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 674, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Студия Abbey Road» преобразован из Markdown в YAML по стандарту знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

