-- Quest: quest-london-2040-tea-smuggling
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-tea-smuggling', 'Контрабанда Чая', 'Игрок закупает настоящий чай за пределами Великобритании, проводит контрабанду, обслуживает элиту и может открыть подпольную чайную.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-tea-smuggling", "title": "Контрабанда Чая", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "trade", "tea"], "topics": ["smuggling", "cultural_flavor"], "related_systems": ["trade-system", "reputation-service", "business-system"], "related_documents": [{"id": "quest-london-2030-fish-and-chips", "relation": "complements"}, {"id": "github-issue-676", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/676", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-028-tea-smuggling.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В Лондоне 2040-х настоящий чай стал дефицитом, что требует сюжетной линии о контрабанде и культурной идентичности.", "goal": "Создать лёгкий торговый квест с возможностью открыть пассивный бизнес и усилить связь с британскими традициями.", "essence": "Игрок закупает настоящий чай за пределами Великобритании, проводит контрабанду, обслуживает элиту и может открыть подпольную чайную.", "key_points": ["Торговая механика с риском и проверками на контрабанду.", "Акцент на культурных традициях и реакции британского общества.", "Опциональный бизнес даёт пассивный доход и укрепляет репутацию у аристократии."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "После смены экономики настоящий чай стал роскошью, а синтетические аналоги не удовлетворяют элиту. Контрабанда возвращает традиции и открывает социальные связи.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Традиция five o''clock tea и культ чайных сервизов.\\n- Отсылки к Восточно-Индийской компании и истории чайных монополий.\\n- Взаимодействие с британскими аристократами и подпольными салонами.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Закупить партии настоящего чая в Индии или другом исходном регионе.\\n2. Провести контрабанду через таможню, избегая досмотра.\\n3. Продать товар аристократам, формируя клиентскую базу.\\n4. По желанию открыть подпольную чайную и наладить регулярные поставки.\\n", "mechanics_links": ["mechanics/economy/smuggling.yaml", "mechanics/business/passive-ventures.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 600 XP и чистая прибыль 5 000 эдди за удачную сделку.\\n- Репутация аристократов +3.\\n- Пассивный доход 2 000 эдди в неделю при открытии чайной.\\n", "mechanics_links": ["mechanics/progression/reputation-system.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с гастрономическими линиями Лондона и может влиять на торговые маршруты и цены на деликатесы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 676, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Контрабанда Чая» структурирован в формате YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

