-- Quest: quest-london-2040-pound-collapse
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-pound-collapse', 'Крах Фунта', 'Игрок проходит через предупреждение, панику и крах, выбирая стратегию сохранения капитала и формируя своё влияние в новой экономике.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-pound-collapse", "title": "Крах Фунта", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "economy", "currency"], "topics": ["financial_crisis", "player_choice"], "related_systems": ["economy-service", "market-system", "reputation-service"], "related_documents": [{"id": "github-issue-677", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/677", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-029-pound-collapse.md", "visibility": "internal", "audience": ["concept", "economy", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экономическая линия Лондона требует сценария резкого обвала фунта и перехода на криптовалюту.", "goal": "Разработать квест, заставляющий игроков принимать финансовые решения в условиях кризиса.", "essence": "Игрок проходит через предупреждение, панику и крах, выбирая стратегию сохранения капитала и формируя своё влияние в новой экономике.", "key_points": ["Событие уровня world-state затрагивает всех игроков в регионе.", "Несколько стратегий поведения дают разные результаты и последствия.", "Переход на «London Coin» меняет доступ к рынкам и ценам."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "На фоне глобальной нестабильности британский фунт стремительно обесценивается. Банк Англии ограничивает выдачу наличных, а доверие рынка падает. Игрок становится свидетелем полной перестройки финансовой системы.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить предупреждение от финансовых аналитиков и СМИ.\\n2. Столкнуться с ограничениями на снятие средств и банковскими очередями.\\n3. Принять решение: обменять фунты на криптовалюту, удержать их или вложиться в активы.\\n4. Пережить крах: фунт теряет 90 % стоимости.\\n5. Адаптироваться: перейти на London Coin и восстановить финансовую стабильность.\\n", "mechanics_links": ["mechanics/economy/market-events.yaml"], "assets": []}, {"id": "choices", "title": "Варианты стратегии", "body": "- **Обмен на криптовалюту:** сохранить большую часть капитала и получить доступ к цифровому рынку.\\n- **Хранение фунтов:** потерять 90 % средств, но заработать сочувствие традиционалистов.\\n- **Инвестиция в активы:** перераспределить капитал в недвижимость, золото и другие товары.\\n", "mechanics_links": ["mechanics/narrative/branching-outcomes.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP.\\n- Финансовый результат зависит от выбранной стратегии (−90 % до +50 %).\\n- Доступ к новой валюте London Coin и экономическим веткам.\\n", "mechanics_links": ["mechanics/economy/currency-switch.yaml"], "assets": []}, {"id": "world_impact", "title": "Влияние на мир", "body": "Квест меняет цены, торговые правила и репутацию финансовых фракций. Влияет на доступ к лондонским рынкам, вызывает цепочки миссий по стабилизации экономики.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 677, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Крах Фунта» перенесён в YAML и адаптирован к шаблону знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

