-- Quest: quest-london-2040-last-gentleman
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2040-last-gentleman', 'Последний Джентльмен', 'Игрок встречает старого джентльмена, осваивает манеры, шьёт костюм на Savile Row и получает доступ к высокому обществу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2040-last-gentleman", "title": "Последний Джентльмен", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "etiquette", "social"], "topics": ["cultural_experience", "social_progression"], "related_systems": ["social-system", "wardrobe-service", "reputation-service"], "related_documents": [{"id": "github-issue-678", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/678", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2040-2060/quest-030-last-gentleman.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальная линия Лондона нуждается в спокойном квесте, погружающем игроков в викторианские традиции.", "goal": "Познакомить игрока с последним хранителем британского этикета, провести через обучение и открыть элитные клубы.", "essence": "Игрок встречает старого джентльмена, осваивает манеры, шьёт костюм на Savile Row и получает доступ к высокому обществу.", "key_points": ["Воссоздание атмосферы лондонских клубов и строгого этикета.", "Мини-игра обучения манерам и подбор гардероба.", "Социальный титул, усиливающий взаимодействие с элитой."]}, "content": {"sections": [{"id": "introduction", "title": "Контекст", "body": "Лондон 2060-х переживает возрождение старых традиций. Последний джентльмен собирает избранных в закрытом клубе, чтобы передать манеры викторианской эпохи и укрепить связи внутри элиты.\\n", "mechanics_links": [], "assets": []}, {"id": "cultural_references", "title": "Региональные отсылки", "body": "- Джентльменский кодекс поведения.\\n- Улица портных Savile Row, где шьют костюмы на заказ.\\n- Чайная церемония ровно в пять вечера.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Познакомиться с наставником в частном клубе.\\n2. Пройти мини-игру на знание правил этикета.\\n3. Заказать персональный костюм на Savile Row и подобрать аксессуары.\\n4. Сопровождать наставника на спектакль в Ковент-Гардене.\\n5. Получить признание клуба и звание джентльмена.\\n", "mechanics_links": ["mechanics/social/etiquette-training.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP.\\n- Титул «Джентльмен» и +5 к переговорам с элитой.\\n- Уникальный костюм и доступ в закрытые клубы.\\n", "mechanics_links": ["mechanics/progression/title-system.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает линейку светского ролевого контента и может использоваться как критерий допуска к дипломатическим сюжетам Лондона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 678, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Последний Джентльмен» перенесён в формат YAML с детализацией по шаблону знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

