-- Quest: quest-london-2061-punk-revolution
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-london-2061-punk-revolution', 'Панк-Революция', 'Игрок формирует панк-банду, будоражит Camden Town и ведёт штурм корпорации под музыку анархии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-london-2061-punk-revolution", "title": "Панк-Революция", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-22T23:58:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["london", "punk", "rebellion"], "topics": ["faction_conflict", "cultural_identity"], "related_systems": ["faction-system", "music-system", "live-event-service"], "related_documents": [{"id": "quest-london-2020-punk-concert", "relation": "precedes"}, {"id": "github-issue-680", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/680", "relation": "migrated_to", "migrated_at": "2025-11-22T23:58:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/london/2061-2077/quest-032-punk-revolution.md", "visibility": "internal", "audience": ["concept", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно масштабное продолжение панк-линии, в котором игроки бросают вызов корпоративному контролю.", "goal": "Организовать революционное событие с концертами, захватом зданий и трансляцией манифеста.", "essence": "Игрок формирует панк-банду, будоражит Camden Town и ведёт штурм корпорации под музыку анархии.", "key_points": ["Фракционный контент с массовыми действиями и музыкальной составляющей.", "Символическое захват корп-объекта и трансляция послания всему городу.", "Долгосрочное влияние на культурную атмосферу Лондона."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Camden Town остаётся сердцем панк-культуры. Корпорации усилили контроль над районами, и уличные музыканты зовут к восстанию, вдохновляясь движением 1970-х.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Региональные отсылки", "body": "- Архивные лозунги «Anarchy in the UK».\\n- Улицы Camden, заполненные граффити «No Future».\\n- Стилизация участников: ирокезы, кожаные куртки с шипами.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Собрать ядро фракции и подготовить концерт-митинг.\\n2. Провести выступление, которое поднимает рейтинг сопротивления.\\n3. Организовать штурм корп-здания с использованием импровизированных средств.\\n4. Захватить медиахабы и транслировать манифест по всему Лондону.\\n5. Совместно отбиваться от сил порядка и закрепить влияние в районе.\\n", "mechanics_links": ["mechanics/factions/rebellion-events.yaml", "mechanics/music/concert-interactions.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP.\\n- Репутация панков +15, корпораций −10.\\n- Культурные изменения: новые флэшмобы, визуальные элементы в городе.\\n", "mechanics_links": ["mechanics/progression/reputation-system.yaml"], "assets": []}, {"id": "consequences", "title": "Последствия", "body": "В случае успеха панк-движение расширяет влияние, изменяет поведение NPC и открывает доступ к уникальным квестам сопротивления.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 680, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "Concept Director", "changes": "Квест «Панк-Революция» оформлен в YAML с учётом фракционных механик и культурного эффекта."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

