INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-detroit-motown-music', 'Детройт 2020-2029 — Музыка Motown', 'Игрок посещает музей Motown, исследует легендарную студию Hitsville U.S.A. и знакомится с наследием soul музыки.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_motown_museum", "text": "Посетить музей Motown на West Grand Boulevard и познакомиться с экспозицией", "type": "travel", "target": "motown_museum", "count": 1, "optional": false}, {"id": "explore_hitsville", "text": "Исследовать дом-студию Hitsville U.S.A. - отправную точку империи Motown", "type": "travel", "target": "hitsville_studio", "count": 1, "optional": false}, {"id": "experience_studio_a", "text": "Прослушать оригинальные треки в Studio A и изучить процесс записи", "type": "interact", "target": "studio_a", "count": 1, "optional": false}, {"id": "learn_motown_history", "text": "Изучить ключевые даты 1959 года и историю артистов Supremes, Temptations, Marvin Gaye", "type": "interact", "target": "historical_exhibition", "count": 1, "optional": false}, {"id": "listen_classics", "text": "Прослушать финальную подборку классических хитов Motown", "type": "interact", "target": "music_library", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": -15, "reputation": {"culture": 25}, "items": [], "unlocks": {"achievements": ["Soul Мастер"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-detroit-motown-music", "title": "Детройт 2020-2029 — Музыка Motown", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-05T00:00:00+00:00", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["motown", "music", "detroit"], "topics": ["cultural-heritage", "museum"], "related_systems": ["narrative-service", "social-progress"], "related_documents": [{"id": "github-issue-339", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/339", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}, {"id": "canon-quest-detroit-motor-city", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/detroit/2020-2029/quest-002-motown-music.md", "visibility": "internal", "audience": ["narrative", "content", "vision"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий Motown существовал как Markdown и не входил в единую базу знаний для нарративных задач.", "goal": "Структурировать ключевые этапы и культурные ссылки квеста «Музыка Motown».", "essence": "Игрок посещает музей Motown, исследует легендарную студию Hitsville U.S.A. и знакомится с наследием soul музыки.", "key_points": ["Визит в Motown Museum и легендарную Studio A раскрывает истоки лейбла.", "История Berry Gordy и артистов Supremes, Marvin Gaye, Stevie Wonder, Jackson 5 закреплена в структуре квеста.", "Награды усиливают культурный прогресс и открывают достижение «Soul Мастер»."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_motown_museum", "text": "Посетить музей Motown на West Grand Boulevard и познакомиться с экспозицией", "type": "travel", "target": "motown_museum", "count": 1, "optional": false}, {"id": "explore_hitsville", "text": "Исследовать дом-студию Hitsville U.S.A. - отправную точку империи Motown", "type": "travel", "target": "hitsville_studio", "count": 1, "optional": false}, {"id": "experience_studio_a", "text": "Прослушать оригинальные треки в Studio A и изучить процесс записи", "type": "interact", "target": "studio_a", "count": 1, "optional": false}, {"id": "learn_motown_history", "text": "Изучить ключевые даты 1959 года и историю артистов Supremes, Temptations, Marvin Gaye", "type": "interact", "target": "historical_exhibition", "count": 1, "optional": false}, {"id": "listen_classics", "text": "Прослушать финальную подборку классических хитов Motown", "type": "interact", "target": "music_library", "count": 1, "optional": false}], "rewards": {"experience": 2000, "money": -15, "reputation": {"culture": 25}, "items": [], "unlocks": {"achievements": ["Soul Мастер"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест предлагает игроку посетить музей Motown и студию Hitsville U.S.A., чтобы погрузиться в историю soul музыки.\\nЛокации раскрывают становление лейбла и его влияние на мировую сцену.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/culture-tours.yaml"], "assets": []}, {"id": "itinerary", "title": "Этапы", "body": "1. Motown Museum на West Grand Boulevard — знакомство с экспозицией и историей лейбла.\\n2. Дом-студия Hitsville U.S.A. — отправная точка империи Motown.\\n3. Studio A — прослушивание оригинальных треков и изучение процесса записи.\\n4. Хронологический блок — ключевые даты 1959 года, артисты Supremes, Temptations, Marvin Gaye и другие.\\n5. Финальное прослушивание классических хитов Motown.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/music-history.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Документ фиксирует дату основания лейбла, уникальную атмосферу небольшого дома-студии и культурное значение\\nsoul музыки для Детройта. Студия A выделена как легендарное пространство, где формировался «Motown Sound».\\n", "mechanics_links": ["mechanics/world/culture-tours.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "XP: 2000, Валюта: -15, Культура: +25. Ачивка «Soul Мастер» награждает игроков за погружение в музыкальную историю.\\nОсновные механики — музыкальная, историческая и культурная, создающие социальный опыт квеста.\\n", "mechanics_links": ["mechanics/world/reputation-system.yaml", "mechanics/economy/rewards.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 51, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.1.0", "date": "2025-12-12", "author": "content_team", "changes": "Связаны механики культурного тура, репутации и наград; обновлена дата."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}, {"version": "1.0.0", "date": "2025-11-05", "author": "ai_brain_manager", "changes": "Квест Motown структурирован для автоматизации и передачи в Vision."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

