INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-houston-hurricane-harvey', 'Хьюстон 2020-2029 — Ураган Харви', 'Игрок исследует мемориалы Harvey, изучает хронологию наводнения и наблюдает, как город восстановился благодаря «Texas Strong».', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_harvey_memorial", "text": "Посещение музея или мемориала Harvey и ознакомление с временной шкалой событий", "type": "interact", "target": "harvey_memorial_museum", "count": 1, "optional": false}, {"id": "view_photo_archives", "text": "Просмотр фотоархивов: затопленные кварталы, эвакуация 30 тысяч жителей", "type": "interact", "target": "harvey_photo_archives", "count": 1, "optional": false}, {"id": "meet_survivors", "text": "Финальный блок — встречи с выжившими и фиксация историй о взаимопомощи", "type": "interact", "target": "harvey_survivors_stories", "count": 1, "optional": false}]'::jsonb, '{"xp": 2500, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "harvey_survivor", "name": "Выживший Harvey"}], "skill_boosts": {"endurance": 25}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-houston-hurricane-harvey", "title": "Хьюстон 2020-2029 — Ураган Харви", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "2.0.0", "last_updated": "2025-11-29T08:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["houston", "disaster", "resilience"], "topics": ["historical-events", "community-response"], "related_systems": ["narrative-service", "investigation-system"], "related_documents": [{"id": "canon-quest-houston-humidity-hell", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-344", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/344", "relation": "migrated_to", "migrated_at": "2025-11-22T20:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/houston/2020-2029/quest-007-hurricane-harvey.md", "visibility": "internal", "audience": ["narrative", "concept", "live-ops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исторический квест об урагане Harvey находился в Markdown и не был структурирован для автоматизированных сценариев.", "goal": "Подготовить YAML-описание катастрофы 2017 года с акцентом на последствия и взаимопомощь.", "essence": "Игрок исследует мемориалы Harvey, изучает хронологию наводнения и наблюдает, как город восстановился благодаря «Texas Strong».", "key_points": ["Воссоздаются события августа 2017 года, масштаб затопления и экономический ущерб.", "Этапы включают экскурсию по мемориалам, просмотр архивных материалов и фиксацию историй жителей.", "Награды повышают выносливость и дают достижение «Выживший Harvey»."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_harvey_memorial", "text": "Посещение музея или мемориала Harvey и ознакомление с временной шкалой событий", "type": "interact", "target": "harvey_memorial_museum", "count": 1, "optional": false}, {"id": "view_photo_archives", "text": "Просмотр фотоархивов: затопленные кварталы, эвакуация 30 тысяч жителей", "type": "interact", "target": "harvey_photo_archives", "count": 1, "optional": false}, {"id": "meet_survivors", "text": "Финальный блок — встречи с выжившими и фиксация историй о взаимопомощи", "type": "interact", "target": "harvey_survivors_stories", "count": 1, "optional": false}], "rewards": {"xp": 2500, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "harvey_survivor", "name": "Выживший Harvey"}], "skill_boosts": {"endurance": 25}}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест переносит игрока в контекст урагана Harvey. Через мемориалы и документальные материалы раскрывается масштаб бедствия,\\nроль спасателей и сила общественной взаимопомощи.\\n", "mechanics_links": [], "assets": []}, {"id": "investigation", "title": "Этапы расследования", "body": "1. Посещение музея или мемориала Harvey и ознакомление с временной шкалой событий.\\n2. Просмотр фотоархивов: затопленные кварталы, эвакуация 30 тысяч жителей.\\n3. Анализ статистики ущерба в $125 млрд и влияния на инфраструктуру.\\n4. Финальный блок — встречи с NPC, рассказывающими о движении «Texas Strong».\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указываются даты 25–29 августа 2017, уровень воды до 1.5 м, количество жертв и финансовые последствия. Отмечена роль волонтёров,\\nлодочных экипажей и общих усилий по восстановлению города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards_mechanics", "title": "Награды и механики", "body": "XP: 4000, Валюта: 0, Параметр «Выносливость»: +25. Игрок получает достижение «Выживший Harvey».\\nОсновные механики — историческое расследование, эмоциональное повествование и социальное взаимодействие.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 344, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-houston-hurricane-harvey"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Ураган Харви» в структурированный формат з