INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-003-tapas-crawl', 'Мадрид: тапас-тур', 'Вечером игрок проходит четыре бара, пробуя хамон, patatas bravas, тортилью и gambas al ajillo.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "start_route", "text": "Стартовать на Plaza Mayor и выбрать маршрут тапас-бара", "type": "visit", "target": "plaza_mayor", "count": 1, "optional": false}, {"id": "bar_1_jamon", "text": "Бар 1: хамон иберико, знакомство с резкой и хранением", "type": "interact", "target": "tapas_bar_1", "count": 1, "optional": false}, {"id": "bar_2_patatas", "text": "Бар 2: patatas bravas с авторским соусом", "type": "interact", "target": "tapas_bar_2", "count": 1, "optional": false}, {"id": "bar_3_tortilla", "text": "Бар 3: тортилья эспаньола и исторические факты", "type": "interact", "target": "tapas_bar_3", "count": 1, "optional": false}, {"id": "bar_4_gambas", "text": "Бар 4: gambas al ajillo, завершение вечера и обсуждение впечатлений", "type": "interact", "target": "tapas_bar_4", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "money": -50, "items": [{"item_id": "achievement_tapas_master", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-003-tapas-crawl", "title": "Мадрид: тапас-тур", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "tapas", "gastronomy"], "topics": ["social-experience", "culinary"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-003-tapas-crawl.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось отразить традицию «ir de tapas» как социальный ритуал Мадрида.", "goal": "Сформировать гастрономический маршрут по барам с ключевыми блюдами и эффектами баффов.", "essence": "Вечером игрок проходит четыре бара, пробуя хамон, patatas bravas, тортилью и gambas al ajillo.", "key_points": ["Показаны характерные закуски и культура вечерних баров.", "Сцены подкрепляют социальные связи и сетевые бонусы.", "Награды включают бафф на здоровье и ачивку «Мастер Тапас»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "start_route", "text": "Стартовать на Plaza Mayor и выбрать маршрут тапас-бара", "type": "visit", "target": "plaza_mayor", "count": 1, "optional": false}, {"id": "bar_1_jamon", "text": "Бар 1: хамон иберико, знакомство с резкой и хранением", "type": "interact", "target": "tapas_bar_1", "count": 1, "optional": false}, {"id": "bar_2_patatas", "text": "Бар 2: patatas bravas с авторским соусом", "type": "interact", "target": "tapas_bar_2", "count": 1, "optional": false}, {"id": "bar_3_tortilla", "text": "Бар 3: тортилья эспаньола и исторические факты", "type": "interact", "target": "tapas_bar_3", "count": 1, "optional": false}, {"id": "bar_4_gambas", "text": "Бар 4: gambas al ajillo, завершение вечера и обсуждение впечатлений", "type": "interact", "target": "tapas_bar_4", "count": 1, "optional": false}], "rewards": {"experience": 1000, "money": -50, "items": [{"item_id": "achievement_tapas_master", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-003 организует вечерний тапас-тур. Игрок посещает серию баров, знакомится с традициями подачи и\\nобщается с местными жителями.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Стартовать на Plaza Mayor и выбрать маршрут тапас-бара.\\n2. Бар 1: хамон иберико, знакомство с резкой и хранением.\\n3. Бар 2: patatas bravas с авторским соусом.\\n4. Бар 3: тортилья эспаньола и исторические факты.\\n5. Бар 4: gambas al ajillo, завершение вечера и обсуждение впечатлений.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Тапасы — малая порция закуски, подаваемая к напиткам.\\n- Барная культура Мадрида подразумевает оживлённые разговоры и поздний ужин.\\n- Многие заведения используют неоновые вывески и традиционные плитки азулежо.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 000 XP.\\n- Валюта: -50 (расходы на закуски и напитки).\\n- Бафф «Сытость» (+25% HP на 10 часов).\\n- Ачивка «Мастер Тапас».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные сцены с выбором порций и комбинаций.\\n- Социальные взаимодействия и сбор контактов.\\n- Система баффов от еды.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает гастрономическую арку Мадрида и синхронизируется с турами других городов (Барселона, Севилья).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 625, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML тапас-тура по Мадриду."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлена структура квеста с objectives, rewards и branches согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

