INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-004-flamenco-night', 'Мадрид: ночь фламенко', 'Игрок посещает клуб фламенко, наблюдает выступление и решает, продолжить ли обучение танцу.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_tablao", "text": "Найти таблао, зарезервировать столик и заказать напиток", "type": "interact", "target": "flamenco_tablao", "count": 1, "optional": false}, {"id": "listen_guitar", "text": "Слушать вступление гитариста, изучая технику пальмирования", "type": "cutscene", "target": "flamenco_guitar", "count": 1, "optional": false}, {"id": "watch_performance", "text": "Насладиться cante jondo и танцем с акцентом на taconeo", "type": "cutscene", "target": "flamenco_dance", "count": 1, "optional": false}, {"id": "optional_lesson", "text": "Выбрать опцию урока фламенко после шоу и получить базовые движения", "type": "choice", "target": "flamenco_lesson", "count": 1, "optional": true}, {"id": "record_impressions", "text": "Сделать запись впечатлений для культурного дневника", "type": "interact", "target": "cultural_diary", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -40, "items": [{"item_id": "achievement_flamenco_connoisseur", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-004-flamenco-night", "title": "Мадрид: ночь фламенко", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "flamenco", "live-show"], "topics": ["cultural-performance", "music"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-004-flamenco-night.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо отразить фламенко как эмоциональный символ Мадрида и андалузских корней.", "goal": "Представить таблао, живой концерт и возможность присоединиться к мастер-классу.", "essence": "Игрок посещает клуб фламенко, наблюдает выступление и решает, продолжить ли обучение танцу.", "key_points": ["Визуализированы элементы guitara flamenca, cante jondo и интенсивные притопы.", {"Вариативность": "просто шоу или участие в уроке."}, "Награды дают культурный рейтинг и ачивку «Ценитель Фламенко»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_tablao", "text": "Найти таблао, зарезервировать столик и заказать напиток", "type": "interact", "target": "flamenco_tablao", "count": 1, "optional": false}, {"id": "listen_guitar", "text": "Слушать вступление гитариста, изучая технику пальмирования", "type": "cutscene", "target": "flamenco_guitar", "count": 1, "optional": false}, {"id": "watch_performance", "text": "Насладиться cante jondo и танцем с акцентом на taconeo", "type": "cutscene", "target": "flamenco_dance", "count": 1, "optional": false}, {"id": "optional_lesson", "text": "Выбрать опцию урока фламенко после шоу и получить базовые движения", "type": "choice", "target": "flamenco_lesson", "count": 1, "optional": true}, {"id": "record_impressions", "text": "Сделать запись впечатлений для культурного дневника", "type": "interact", "target": "cultural_diary", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -40, "items": [{"item_id": "achievement_flamenco_connoisseur", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-004 погружает игрока в таблао фламенко. Необходимо наблюдать выступление и прочувствовать\\nэмоциональный накал традиционного искусства.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Найти таблао, зарезервировать столик и заказать напиток.\\n2. Слушать вступление гитариста, изучая технику пальмирования.\\n3. Насладиться cante jondo и танцем с акцентом на taconeo.\\n4. Выбрать опцию «урок фламенко» после шоу и получить базовые движения.\\n5. Сделать запись впечатлений для культурного дневника.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Фламенко происходит из Андалусии, но мадридские таблао считаются одними из лучших.\\n- Характерны яркие костюмы, красные платья и резкие ритмы.\\n- Вечерние представления сопровождаются традиционной кухней и напитками.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Валюта: -40 (вход и заказ).\\n- Культура +20.\\n- Ачивка «Ценитель Фламенко».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Культурные сцены с музыкальными триггерами.\\n- Вариативный выбор с участием в уроке.\\n- Система культурных баффов и социального рейтинга.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает культурную арку Испании и соединяет её с музыкальными событиями Севильи и Гранады.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 626, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест ночи фламенко в Мадриде."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлена структура квеста с objectives, rewards и branches согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

