INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-005-retiro-park', 'Мадрид: парк Ретиро', 'Игрок гуляет по парку, арендует лодку и открывает культурные заметки о монументе Альфонсо XII.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_park", "text": "Войти через ворота Puerta de Alcalá и получить карту парка", "type": "visit", "target": "retiro_park_entrance", "count": 1, "optional": false}, {"id": "visit_crystal_palace", "text": "Дойти до Хрустального дворца, изучить выставочную инсталляцию", "type": "visit", "target": "crystal_palace", "count": 1, "optional": false}, {"id": "rent_boat", "text": "Арендовать лодку на Estanque Grande и насладиться прогулкой по воде", "type": "interact", "target": "retiro_pond", "count": 1, "optional": false}, {"id": "visit_monument", "text": "Посетить памятник Альфонсо XII и сделать панорамные снимки", "type": "interact", "target": "alfonso_xii_monument", "count": 1, "optional": false}, {"id": "finish_walk", "text": "Завершить прогулку чтением в тени платанов", "type": "rest", "target": "park_benches", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": -10, "items": [{"item_id": "achievement_master_of_retiro", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-005-retiro-park", "title": "Мадрид: парк Ретиро", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "retiro-park", "leisure"], "topics": ["urban-nature", "relaxation"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-005-retiro-park.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Парковая линия Мадрида требовала квеста, отражающего ценность Ретиро как зелёных лёгких города.", "goal": "Предложить расслабляющий маршрут по ключевым точкам парка с акцентом на Хрустальный дворец и пруд.", "essence": "Игрок гуляет по парку, арендует лодку и открывает культурные заметки о монументе Альфонсо XII.", "key_points": ["Описаны 125 гектаров парка и его популярность среди жителей.", "Включены Хрустальный дворец, пруд и лодочная активность.", "Награды дают релаксационный бафф и ачивку «Хозяин Ретиро»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_park", "text": "Войти через ворота Puerta de Alcalá и получить карту парка", "type": "visit", "target": "retiro_park_entrance", "count": 1, "optional": false}, {"id": "visit_crystal_palace", "text": "Дойти до Хрустального дворца, изучить выставочную инсталляцию", "type": "visit", "target": "crystal_palace", "count": 1, "optional": false}, {"id": "rent_boat", "text": "Арендовать лодку на Estanque Grande и насладиться прогулкой по воде", "type": "interact", "target": "retiro_pond", "count": 1, "optional": false}, {"id": "visit_monument", "text": "Посетить памятник Альфонсо XII и сделать панорамные снимки", "type": "interact", "target": "alfonso_xii_monument", "count": 1, "optional": false}, {"id": "finish_walk", "text": "Завершить прогулку чтением в тени платанов", "type": "rest", "target": "park_benches", "count": 1, "optional": false}], "rewards": {"experience": 600, "money": -10, "items": [{"item_id": "achievement_master_of_retiro", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-005 приглашает игрока исцарать парк Ретиро. Маршрут сочетает архитектурные достопримечательности\\nи активный отдых на воде.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Войти через ворота Puerta de Alcalá и получить карту парка.\\n2. Дойти до Хрустального дворца, изучить выставочную инсталляцию.\\n3. Арендовать лодку на Estanque Grande и насладиться прогулкой по воде.\\n4. Посетить памятник Альфонсо XII и сделать панорамные снимки.\\n5. Завершить прогулку чтением в тени платанов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Parque del Retiro площадью 125 гектаров возник как королевский сад XVII века.\\n- Хрустальный дворец построен в 1887 году из стекла и металла.\\n- Парк служит местом отдыха и культурных мероприятий, включая книжные фестивали.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 600 XP.\\n- Валюта: -10 (аренда лодки).\\n- Бафф «Релакс» (+15 к восстановлению выносливости).\\n- Ачивка «Хозяин Ретиро».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристические сцены с leisurely pace.\\n- Активность на воде и мини-игра гребли.\\n- Релаксационные баффы для городских миссий.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает сетку городских парков Европы, дополняя линии Берлина и Парижа.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 627, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест отдыха в парке Ретиро."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлена структура квеста с objectives, rewards и branches согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

