INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-atlanta-2029-007-hip-hop', 'Атланта: столица хип-хопа', 'Игрок посещает студии Patchwerk и Magic City, встречается с артистами и выбирает стратегию продвижения музыкального наследия.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-atlanta-2029-007-hip-hop", "title": "Атланта: столица хип-хопа", "document_type": "canon", "category": "city-quest", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:16:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["atlanta", "music", "hip-hop"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "economy-service"], "related_documents": [{"id": "github-issue-297", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/297", "relation": "migrated_to", "migrated_at": "2025-11-22T19:16:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-007-hip-hop-capital.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик квеста о хип-хопе описывал сцену без ветвлений и влияния на музыкальные сервисы.", "goal": "Построить сценарий, где игрок исследует студии, клубы и решает, как продвигать ATL trap-культуру.", "essence": "Игрок посещает студии Patchwerk и Magic City, встречается с артистами и выбирает стратегию продвижения музыкального наследия.", "key_points": ["Отражена роль OutKast, T.I., Migos и Lil Baby.", "Включены мини-игры по созданию бита и freestyle батлу.", "Выбор влияет на музыкальную экономику и социальную репутацию."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ATLANTA-2029-007`\\n- Формат: культурный сценарий, сложность низкая, solo, длительность 2–4 часа.\\n- Локации: Patchwerk Studios, Trap Music Museum, Magic City.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "route", "title": "Музыкальный маршрут", "body": "1. Прослушать подборку ATL hip-hop и собрать вдохновение.\\n2. Посетить студию, участвовать в записи hook или бита.\\n3. Заглянуть в Trap Music Museum и изучить историю жанра.\\n4. Завершить вечер в Magic City, наблюдая за клубной культурой.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "choices", "title": "Продвижение сцены", "body": "- Продвинуть социальную инициативу артистов и поддержать молодые таланты.\\n- Организовать trap-фестиваль и усилить туристический поток.\\n- Закрепить бренд ATL как «Hollywood of the South» через медиа-партнёров.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 1500 XP, расходы 40 кредитов на мероприятия.\\n- Показатель «Культура» +20, ачивка «ATL Rapper».\\n- Бафф «Beat Flow» (+15% к скорости восстановления энергии на 6 часов).\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 297, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста о хип-хоп столице Атланты в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED