INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-006-real-madrid', 'Мадрид: матч «Реал Мадрид»', 'Игрок покупает билет, погружается в атмосферу «Hala Madrid!» и завершает визит в клубном музее.', 'event', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "buy_ticket", "text": "Приобрести билет и атрибутику Los Blancos", "type": "pay", "target": "real_madrid_ticket_office", "count": 1, "optional": false}, {"id": "enter_stadium", "text": "Зайти на стадион, занять место среди 81 000 фанатов", "type": "visit", "target": "santiago_bernabeu", "count": 1, "optional": false}, {"id": "watch_match", "text": "Пережить 90 минут матча, синхронизируя кричалки Hala Madrid!", "type": "cutscene", "target": "football_match", "count": 1, "optional": false}, {"id": "visit_museum", "text": "Посетить музей клуба, рассмотреть 14 Кубков чемпионов и легендарные персоны", "type": "visit", "target": "real_madrid_museum", "count": 1, "optional": false}, {"id": "get_souvenir", "text": "Зафиксировать впечатления и получить цифровой сувенир", "type": "interact", "target": "souvenir_shop", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -80, "items": [{"item_id": "achievement_madridista", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-006-real-madrid", "title": "Мадрид: матч «Реал Мадрид»", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "real-madrid", "football"], "topics": ["live-events", "sports-culture"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-006-real-madrid.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось показать культовый футбольный опыт на Сантьяго Бернабеу и фанатскую культуру «Реал Мадрид».", "goal": "Провести игрока через подготовку, матч и музей трофеев клуба.", "essence": "Игрок покупает билет, погружается в атмосферу «Hala Madrid!» и завершает визит в клубном музее.", "key_points": ["Подчёркнута история клуба и рекордные 14 побед в ЛЧ.", "Сцены стадиона, фанатских кричалок и музея.", "Награды дают спортивный рейтинг и титул «Мадридиста»."]}, "quest_definition": {"quest_type": "event", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "buy_ticket", "text": "Приобрести билет и атрибутику Los Blancos", "type": "pay", "target": "real_madrid_ticket_office", "count": 1, "optional": false}, {"id": "enter_stadium", "text": "Зайти на стадион, занять место среди 81 000 фанатов", "type": "visit", "target": "santiago_bernabeu", "count": 1, "optional": false}, {"id": "watch_match", "text": "Пережить 90 минут матча, синхронизируя кричалки Hala Madrid!", "type": "cutscene", "target": "football_match", "count": 1, "optional": false}, {"id": "visit_museum", "text": "Посетить музей клуба, рассмотреть 14 Кубков чемпионов и легендарные персоны", "type": "visit", "target": "real_madrid_museum", "count": 1, "optional": false}, {"id": "get_souvenir", "text": "Зафиксировать впечатления и получить цифровой сувенир", "type": "interact", "target": "souvenir_shop", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -80, "items": [{"item_id": "achievement_madridista", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-006 позволяет посетить домашний матч «Реал Мадрид» на обновлённом Сантьяго Бернабеу. Игрок\\nпроходит весь путь болельщика — от покупки билета до экскурсии по клубному музею.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Приобрести билет и атрибутику «Los Blancos».\\n2. Зайти на стадион, занять место среди 81 000 фанатов.\\n3. Пережить 90 минут матча, синхронизируя кричалки «Hala Madrid!».\\n4. Посетить музей клуба, рассмотреть 14 Кубков чемпионов и легендарные персоны.\\n5. Зафиксировать впечатления и получить цифровой сувенир.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Сантьяго Бернабеу — культовый стадион в центре Мадрида с модульной крышей.\\n- Клуб ассоциируется с белой формой и статусом «Los Blancos».\\n- Фанаты известны своей страстью и песней «Hala Madrid».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Валюта: -80 (билет и атрибутика).\\n- Спортивный рейтинг +20.\\n- Ачивка «Мадридиста».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Спортивное событие с динамическим аудио.\\n- Социальные сцены фанатских кричалок.\\n- Система коллекционирования клубных трофеев.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест входит в спортивную арку Мадрида и связывается с глобальными футбольными сюжетами (Барселона, Манчестер).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {