INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-007-plaza-mayor', 'Мадрид: Пласа-Майор', 'Игрок входит через арку, исследует площади и завершает визит кофе под аркадами.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_plaza", "text": "Войти на площадь через одну из девяти арок и изучить план 129×94 метра", "type": "visit", "target": "plaza_mayor", "count": 1, "optional": false}, {"id": "examine_statue", "text": "Осмотреть статую Филиппа III и собрать исторические заметки", "type": "investigate", "target": "philip_iii_statue", "count": 1, "optional": false}, {"id": "visit_cafe", "text": "Посетить кафе под аркадами, попробовать местные деликатесы", "type": "interact", "target": "plaza_cafe", "count": 1, "optional": false}, {"id": "activate_ar_scene", "text": "Активировать AR-сцену с событиями инквизиции и реконструкциями праздников", "type": "cutscene", "target": "ar_historical_reconstruction", "count": 1, "optional": false}, {"id": "finish_visit", "text": "Завершить квест вечерним концертом или рынком ремёсел", "type": "interact", "target": "evening_event", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": -20, "items": [{"item_id": "achievement_master_of_mayor", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-007-plaza-mayor", "title": "Мадрид: Пласа-Майор", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "plaza-mayor", "history"], "topics": ["cultural-tourism", "urban-heritage"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-007-plaza-mayor.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось подчеркнуть историческую значимость Plaza Mayor и её контраст между прошлым и настоящим.", "goal": "Организовать прогулку по площади с акцентом на архитектуру, кафе и сложную историю инквизиции.", "essence": "Игрок входит через арку, исследует площади и завершает визит кофе под аркадами.", "key_points": ["Закрытая площадь XVII века с красными фасадами и статуей Филиппа III.", "Упомянуты казни инквизиции и современные культурные мероприятия.", "Награды добавляют исторический рейтинг и титул «Хозяин Майора»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_plaza", "text": "Войти на площадь через одну из девяти арок и изучить план 129×94 метра", "type": "visit", "target": "plaza_mayor", "count": 1, "optional": false}, {"id": "examine_statue", "text": "Осмотреть статую Филиппа III и собрать исторические заметки", "type": "investigate", "target": "philip_iii_statue", "count": 1, "optional": false}, {"id": "visit_cafe", "text": "Посетить кафе под аркадами, попробовать местные деликатесы", "type": "interact", "target": "plaza_cafe", "count": 1, "optional": false}, {"id": "activate_ar_scene", "text": "Активировать AR-сцену с событиями инквизиции и реконструкциями праздников", "type": "cutscene", "target": "ar_historical_reconstruction", "count": 1, "optional": false}, {"id": "finish_visit", "text": "Завершить квест вечерним концертом или рынком ремёсел", "type": "interact", "target": "evening_event", "count": 1, "optional": false}], "rewards": {"experience": 600, "money": -20, "items": [{"item_id": "achievement_master_of_mayor", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-007 проводит игрока через Пласа-Майор, показывая её как сердце старого Мадрида и место\\nпересечения историй — от королевских торжеств до тёмных страниц инквизиции.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Войти на площадь через одну из девяти арок и изучить план 129×94 метра.\\n2. Осмотреть статую Филиппа III и собрать исторические заметки.\\n3. Посетить кафе под аркадами, попробовать местные деликатесы.\\n4. Активировать AR-сцену с событиями инквизиции и реконструкциями праздников.\\n5. Завершить квест вечерним концертом или рынком ремёсел.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Plaza Mayor создана в 1619 году и окружена гомогенными фасадами с балконами.\\n- Площадь пережила пожары и реставрации, сохранив центральный характер.\\n- Инквизиция использовала её для публичных аутодафе, что сохраняется в коллективной памяти.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 600 XP.\\n- Валюта: -20 (кофе и угощения).\\n- Исторический рейтинг +10.\\n- Ачивка «Хозяин Майора».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристические сцены в городском центре.\\n- AR-реконструкции исторических событий.\\n- Система исторических заметок для коллекции города.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест входит в историческую арку Мадрида и сопоставляется с площадями Толедо и Саламанки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 629, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест прогулки по Пласа-Майор."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлена структура квеста с objectives, rewards и branches согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

