INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-008-churros-chocolate', 'Мадрид: чуррос с шоколадом', 'Игрок заказывает порцию чуррос, макает их в горячий шоколад и получает энерго-бафф.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_chocolateria", "text": "Найти Chocolatería San Ginés или аналогичную чуррерию", "type": "visit", "target": "chocolateria_san_gines", "count": 1, "optional": false}, {"id": "order_churros", "text": "Заказать порцию чуррос и кружку густого горячего шоколада", "type": "pay", "target": "churros_order", "count": 1, "optional": false}, {"id": "watch_preparation", "text": "Наблюдать, как чуррос жарятся в масле и посыпаются сахаром", "type": "cutscene", "target": "churros_preparation", "count": 1, "optional": false}, {"id": "taste_churros", "text": "Макнуть чуррос в шоколад, оценить вкус и записать заметки", "type": "interact", "target": "churros_tasting", "count": 1, "optional": false}, {"id": "talk_locals", "text": "Поговорить с местными о традициях ночных закусок", "type": "talk", "target": "local_patrons", "count": 2, "optional": false}]'::jsonb, '{"experience": 500, "money": -10, "items": [{"item_id": "achievement_churros_gourmet", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-008-churros-chocolate", "title": "Мадрид: чуррос с шоколадом", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "churros", "breakfast"], "topics": ["gastronomic-experience", "slice-of-life"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-008-churros-chocolate.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно было отразить культовый мадридский завтрак и атмосферу Chocolatería San Ginés.", "goal": "Создать короткий квест, где игрок пробует чуррос с густым шоколадом и узнаёт традицию ночных посетителей.", "essence": "Игрок заказывает порцию чуррос, макает их в горячий шоколад и получает энерго-бафф.", "key_points": ["Подчеркивается легендарная chocolatería 1894 года.", "Описаны вкусовые детали и контекст ночной жизни.", "Награды дают энергию и ачивку «Чуррос-Гурман»."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_chocolateria", "text": "Найти Chocolatería San Ginés или аналогичную чуррерию", "type": "visit", "target": "chocolateria_san_gines", "count": 1, "optional": false}, {"id": "order_churros", "text": "Заказать порцию чуррос и кружку густого горячего шоколада", "type": "pay", "target": "churros_order", "count": 1, "optional": false}, {"id": "watch_preparation", "text": "Наблюдать, как чуррос жарятся в масле и посыпаются сахаром", "type": "cutscene", "target": "churros_preparation", "count": 1, "optional": false}, {"id": "taste_churros", "text": "Макнуть чуррос в шоколад, оценить вкус и записать заметки", "type": "interact", "target": "churros_tasting", "count": 1, "optional": false}, {"id": "talk_locals", "text": "Поговорить с местными о традициях ночных закусок", "type": "talk", "target": "local_patrons", "count": 2, "optional": false}], "rewards": {"experience": 500, "money": -10, "items": [{"item_id": "achievement_churros_gourmet", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-008 знакомит игрока с традицией «churros con chocolate». Нужно посетить знаменитую чуррерию и\\nпопробовать десерт после ночной фиесты или ранним утром.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Найти Chocolatería San Ginés или аналогичную чуррерию.\\n2. Заказать порцию чуррос и кружку густого горячего шоколада.\\n3. Наблюдать, как чуррос жарятся в масле и посыпаются сахаром.\\n4. Макнуть чуррос в шоколад, оценить вкус и записать заметки.\\n5. Поговорить с местными о традициях ночных закусок.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Чуррос — хрустящие полоски теста, подаваемые с сахаром.\\n- Шоколад густой, почти как пудинг, подается в керамических чашках.\\n- Сан-Хинес — легендарное заведение, популярное и днём, и после ночных клубов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 500 XP.\\n- Валюта: -10.\\n- Бафф «Сладкий заряд»: +15% энергии на 4 часа.\\n- Ачивка «Чуррос-Гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные сцены и дегустация.\\n- Социальные взаимодействия с ночной аудиторией.\\n- Система краткосрочных баффов от еды.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест расширяет гастрономическую арку Мадрида и связывает её с завтраками Барселоны и Лиссабона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 630, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест дегустации чуррос с шоколадом."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлена структура квеста с objectives, rewards и branches согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

