INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-madrid-2029-009-siesta-culture', 'Мадрид: культура сиесты', 'Игрок следует расписанию местных жителей, узнаёт причины традиции и получает релаксационный бафф.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "have_lunch", "text": "Пообедать около 14:00 в традиционном ресторане", "type": "interact", "target": "traditional_restaurant", "count": 1, "optional": false}, {"id": "try_shopping", "text": "Попытаться сделать покупки - убедиться, что магазины закрыты до 17:00", "type": "interact", "target": "closed_shops", "count": 3, "optional": false}, {"id": "take_siesta", "text": "Найти тихое место для дневного сна и провести 90-120 минут отдыха", "type": "rest", "target": "siesta_location", "count": 1, "optional": false}, {"id": "evening_coffee", "text": "Проснуться, выпить кофе и отметить, как город оживает к вечеру", "type": "interact", "target": "evening_cafe", "count": 1, "optional": false}, {"id": "late_dinner", "text": "Завершить день ужином около полуночи и прогулкой по ночному Мадриду", "type": "interact", "target": "night_madrid", "count": 1, "optional": false}]'::jsonb, '{"experience": 800, "money": 0, "items": [{"item_id": "achievement_siesta_adept", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-madrid-2029-009-siesta-culture", "title": "Мадрид: культура сиесты", "document_type": "canon", "category": "quests", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["madrid", "siesta", "lifestyle"], "topics": ["social-routine", "relaxation"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/madrid/2020-2029/quest-009-siesta-culture.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось объяснить игроку испанский ритм жизни и социальное значение сиесты.", "goal": "Показать цикл дня: поздний обед, закрытые магазины, дневной сон и ночную активность.", "essence": "Игрок следует расписанию местных жителей, узнаёт причины традиции и получает релаксационный бафф.", "key_points": ["Отражена жара середины дня и привычка закрывать бизнесы.", "Показаны поздний ужин и ночная жизнь.", "Награды дают энергию и культурное понимание."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "have_lunch", "text": "Пообедать около 14:00 в традиционном ресторане", "type": "interact", "target": "traditional_restaurant", "count": 1, "optional": false}, {"id": "try_shopping", "text": "Попытаться сделать покупки - убедиться, что магазины закрыты до 17:00", "type": "interact", "target": "closed_shops", "count": 3, "optional": false}, {"id": "take_siesta", "text": "Найти тихое место для дневного сна и провести 90-120 минут отдыха", "type": "rest", "target": "siesta_location", "count": 1, "optional": false}, {"id": "evening_coffee", "text": "Проснуться, выпить кофе и отметить, как город оживает к вечеру", "type": "interact", "target": "evening_cafe", "count": 1, "optional": false}, {"id": "late_dinner", "text": "Завершить день ужином около полуночи и прогулкой по ночному Мадриду", "type": "interact", "target": "night_madrid", "count": 1, "optional": false}], "rewards": {"experience": 800, "money": 0, "items": [{"item_id": "achievement_siesta_adept", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест MADRID-2029-009 погружает игрока в социальный ритм сиесты. Нужно пережить дневной перерыв, оценить\\nпреимущества отдыха и адаптироваться к позднему ужину.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Пообедать около 14:00 в традиционном ресторане.\\n2. Попытаться сделать покупки — убедиться, что магазины закрыты до 17:00.\\n3. Найти тихое место для дневного сна и провести 90–120 минут отдыха.\\n4. Проснуться, выпить кофе и отметить, как город оживает к вечеру.\\n5. Завершить день ужином около полуночи и прогулкой по ночному Мадриду.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Сиеста зародилась из-за дневной жары и до сих пор влияет на расписание бизнеса.\\n- Мадридцы активно живут ночью: бары и рестораны работают до позднего времени.\\n- Выражение «mañana» отражает более расслабленное отношение ко времени.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 800 XP.\\n- Релакс +20.\\n- Бафф «После сиесты»: +30% энергии.\\n- Ачивка «Адепт сиесты».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Режим дня и управление ресурсами энергии.\\n- Культурные сцены и социальные заметки.\\n- Система баффов от отдыха.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает культурную арку Испании и связывается с ночной жизнью Барселоны и Валенсии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 631, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест культуры сиесты."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлена структура квеста с objectives, rewards и branches согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

