-- Quest: canon-quest-paris-2020-2029-champs-elysees
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-2020-2029-champs-elysees', 'Париж 2020-2029 — «Елисейские Поля»', 'Прогулка совмещает туристический маршрут, шопинг и финальную панораму с вершины Арки.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_concorde", "text": "Прибыть на Place de la Concorde и получить brief от туристического ИИ", "type": "interact", "target": "place_concorde", "count": 1, "optional": false}, {"id": "walk_avenue", "text": "Пройти по Елисейским полям от площади Согласия до Триумфальной арки", "type": "navigate", "target": "champs_elysees_avenue", "count": 1, "optional": false}, {"id": "shop_or_window_shop", "text": "Выбрать между window-shopping и дорогими покупками", "type": "interact", "target": "champs_elysees_shopping", "count": 1, "optional": false}, {"id": "reach_arc", "text": "Добраться до Триумфальной арки", "type": "navigate", "target": "arc_de_triomphe", "count": 1, "optional": false}, {"id": "view_panorama", "text": "Подняться на вершину Арки и насладиться панорамой", "type": "interact", "target": "arc_de_triomphe_top", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "currency": 0, "items": [], "reputation": {"Paris_Tourism": 8}, "flags": ["champs_elysees_explored", "style_increased"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-2020-2029-champs-elysees", "title": "Париж 2020-2029 — «Елисейские Поля»", "document_type": "canon", "category": "timeline-author", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["paris", "luxury", "avenue"], "topics": ["cityscape", "lifestyle"], "related_systems": ["narrative-service", "commerce-system"], "related_documents": [{"id": "github-issue-614", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/614", "relation": "migrated_to", "migrated_at": "2025-11-22T23:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/paris/2020-2029/quest-004-champs-elysees.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Парижу нужен лёгкий квест, демонстрирующий роскошь и символику Елисейских Полей.", "goal": "Провести игрока по авеню от площади Согласия до Триумфальной арки с акцентом на люксовые активности.", "essence": "Прогулка совмещает туристический маршрут, шопинг и финальную панораму с вершины Арки.", "key_points": ["Показаны ключевые точки авеню и выбор между window-shopping и дорогими покупками.", "Встроен контекст парада 14 июля и звездной развилки из 12 улиц.", "Награды повышают стиль, но могут требовать крупных расходов."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_concorde", "text": "Прибыть на Place de la Concorde и получить brief от туристического ИИ", "type": "interact", "target": "place_concorde", "count": 1, "optional": false}, {"id": "walk_avenue", "text": "Пройти по Елисейским полям от площади Согласия до Триумфальной арки", "type": "navigate", "target": "champs_elysees_avenue", "count": 1, "optional": false}, {"id": "shop_or_window_shop", "text": "Выбрать между window-shopping и дорогими покупками", "type": "interact", "target": "champs_elysees_shopping", "count": 1, "optional": false}, {"id": "reach_arc", "text": "Добраться до Триумфальной арки", "type": "navigate", "target": "arc_de_triomphe", "count": 1, "optional": false}, {"id": "view_panorama", "text": "Подняться на вершину Арки и насладиться панорамой", "type": "interact", "target": "arc_de_triomphe_top", "count": 1, "optional": false}], "rewards": {"experience": 600, "currency": 0, "items": [], "reputation": {"Paris_Tourism": 8}, "flags": ["champs_elysees_explored", "style_increased"]}, "branches": []}, "content": {"sections": [{"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Настрой квеста\\nЛокация: Париж, Place de la Concorde\\nОписание: Игрок прибывает на Place de la Concorde, получает brief от туристического ИИ и выбирает темп прогулки. Аудиогид подсвечивает исторические факты и напоминает о грядущем параде.\\nЦели:\\n  - Прибыть на Place de la Concorde\\n  - Встретиться с туристическим ИИ\\n  - Получить brief о маршруте по Елисейским полям\\n  - Активировать аудиогид с историческими фактами\\n  - Выбрать темп прогулки\\n  - Узнать о грядущем параде 14 июля\\n  - Проверка навыков: Choice 0.2+ для выбора темпа, Awareness 0.3+ для изучения информации\\n\\nЭтап 2: Маршрут по Елисейским полям\\nЛокация: Париж, Елисейские поля (Champs-Élysées), 1,9-километровая авеню\\nОписание: Пройти вдоль 1,9-километровой авеню, посещая кафе, бутики и pop-up сцены. Выполнить побочные активности: примерка люксового аксессуара или фотографирование витрин.\\nЦели:\\n  - Начать прогулку по Елисейским полям\\n  - Посетить кафе на авеню\\n  - Посетить люксовые бутики (Louis Vuitton, Cartier и другие)\\n  - Изучить pop-up сцены\\n  - Выполнить побочные активности (примерка люксового аксессуара или фотографирование витрин)\\n  - Собрать истории NPC о парадах и ночной подсветке\\n  - Насладиться атмосферой роскоши авеню\\n  - Проверка навыков: Social 0.3+ для взаимодействия с NPC, Shopping 0.3+ для шопинга\\n\\nЭтап 3: Подъём на Триумфальную арку\\nЛокация: Париж, Arc de Triomphe\\nОписание: Подняться на Arc de Triomphe и активировать панорамный скан 12 расходящихся авеню.\\nЦели:\\n  - Дойти до Arc de Triomphe в конце Елисейских полей\\n  - Подняться на вершину Триумфальной арки\\n  - Активировать панорамный скан\\n  - Наблюдать 12 расходящихся авеню с вершины арки\\n  - Сделать панорамные снимки Парижа\\n  - Завершить квест\\n  - Проверка навыков: Photography 0.3+ для панорамных снимков, Observation 0.3+ для наблюдений\\n", "mechanics_links": [], "assets": []}, {"id": "regional-details", "title": "Региональные детали", "body": "- Деревья и сезонные инсталляции вдоль авеню.\\n- Флаговые магазины Louis Vuitton, Cartier и Galeries Lafayette.\\n- Подсветка и праздничные баннеры ко Дню Бастилии.\\n", "mechanics_links": [], "assets": []}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Туристический ИИ (Voice AI)\\nДиалог 1 (начальный):\\n  - AI: \\"Bonjour! Добро пожаловать на Place de la Concorde! Вы готовы к прогулке по самым знаменитым Елисейским полям?\\"\\n  - Игрок: \\"Да, расскажите о маршруте.\\"\\n  - AI: \\"Champs-Élysées — это 1,9 километра роскоши и истории. Вы пройдёте мимо самых знаменитых бутиков мира — Louis Vuitton, Cartier, Galeries Lafayette.\\"\\n  - Игрок: \\"Что особенного в Елисейских полях?\\"\\n  - AI: \\"Это символ Парижа! Здесь проходят парады 14 июля, здесь собираются парижане и туристы. А в конце маршрута вас ждёт Триумфальная арка с панорамой 12 авеню!\\"\\n\\nПродавец в Louis Vuitton (Мари)\\nДиалог 2:\\n  - Мари: \\"Bonjour! Добро пожаловать в Louis Vuitton! Вы ищете что-то особенное?\\"\\n  - Игрок: \\"Давайте посмотрим ваши коллекции.\\"\\n  - Мари: \\"У нас есть эксклюзивные сумки, аксессуары и одежда. Это настоящая роскошь французского стиля.\\"\\n  - Игрок: \\"Что вы рекомендуете?\\"\\n  - Мари: \\"Я рекомендую посмотреть на нашу классическую коллекцию. Это инвестиция в стиль и качество, которое прослужит вам долгие годы.\\"\\n\\nПосетитель кафе (Жан)\\nДиалог 3:\\n  - Жан: \\"Прекрасный день для прогулки по Елисейским полям, не так ли?\\"\\n  - Игрок: \\"Да, здесь особая атмосфера.\\"\\n  - Жан: \\"О, это только начало! Вечером авеню подсвечивается тысячами огней. А 14 июля здесь проходит грандиозный парад. Это незабываемо!\\"\\n  - Игрок: \\"Расскажите подробнее.\\"\\n  - Жан: \\"Парад проходит от Триумфальной арки до Place de la Concorde. Это символ единства и гордости Франции. Весь мир смотрит на этот парад!\\"\\n\\nПродавец в Cartier (Софи)\\nДиалог 4:\\n  - Софи: \\"Bienvenue в Cartier! У нас самые изысканные ювелирные изделия и часы.\\"\\n  - Игрок: \\"Что особенного в Cartier?\\"\\n  - Софи: \\"Cartier — это наследие французской роскоши. Наши изделия носят королевские семьи и знаменитости по всему миру.\\"\\n  - Игрок: \\"А что вы можете предложить?\\"\\n  - Софи: \\"У нас есть коллекции колец, часов и аксессуаров. Каждое изделие создано с вниманием к деталям. Это не просто украшение — это произведение искусства.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Темп прогулки\\nУсловие: При настройке квеста\\nВарианты:\\n  - Медленный темп: больше времени на изучение, разблокируется больше информации и разблокируется достижение \\"Неторопливый исследователь\\"\\n  - Средний темп: баланс между изучением и скоростью\\n  - Быстрый темп: быстро пройти маршрут, сэкономить время, но меньше информации\\n\\nВыбор 2: Подход к шопингу\\nУсловие: При посещении бутиков\\nВарианты:\\n  - Полноценный шопинг: покупать люксовые вещи, -10 000 еддис, +20 к стилю, разблокируется достижение \\"Люксовый шопер\\"\\n  - Экономный маршрут: только window-shopping, 0 еддис, +10 к стилю, разблокируется достижение \\"Окно-шопер\\"\\n  - Сбалансированный подход: купить что-то небольшое, -3 000 еддис, +15 к стилю, разблокируется достижение \\"Умеренный шопер\\"\\n\\nВыбор 3: Побочные активности\\nУсловие: При прогулке по авеню\\nВарианты:\\n  - Примерка люксового аксессуара: попробовать что-то дорогое, получить опыт роскоши, разблокируется достижение \\"Примерка роскоши\\"\\n  - Фотографирование витрин: делать снимки витрин бутиков, разблокируется достижение \\"Фотограф витрин\\"\\n  - Оба варианта: получить опыт обоих активностей, но потратить больше времени\\n\\nВыбор 4: Подход к панораме\\nУсловие: На вершине Arc de Triomphe\\nВарианты:\\n  - Детальное наблюдение: изучить все 12 авеню, получить полную информацию о Париже, разблокируется достижение \\"Панорамный обозреватель\\"\\n  - Быстрый осмотр: быстро посмотреть и сделать снимки, сэкономить время\\n  - Сбалансированный подход: оптимальный баланс наблюдения и времени\\n", "mechanics_links": [], "assets": []}, {"id": "skill_checks", "title": "Проверки навыков", "body": "Проверка 1: Choice (Этап 1)\\nТребование: 0.2+\\nУспех: Игрок выбирает оптимальный темп прогулки, разблокируется дополнительная информация о маршруте\\nПровал: Игрок получает стандартный темп прогулки\\n\\nПроверка 1b: Awareness (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок внимательно слушает информацию от аудиогида, разблокируется дополнительная информация о истории Елисейских полей\\nПровал: Игрок получает только базовую информацию\\n\\nПроверка 2: Social (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок успешно взаимодействует с NPC, собирает больше историй о парадах и ночной подсветке, разблокируется достижение \\"Собиратель историй\\"\\nПровал: Игрок получает только базовые истории\\n\\nПроверка 2b: Shopping (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок успешно выбирает люксовые вещи, получает скидку 10% на покупки и разблокируется достижение \\"Мастер шопинга\\"\\nПровал: Игрок платит стандартные цены\\n\\nПроверка 3: Photography (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок делает качественные панорамные снимки, разблокируется достижение \\"Панорамный фотограф\\" и +5 XP\\nПровал: Игрок делает обычные снимки\\n\\nПроверка 3b: Observation (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок внимательно наблюдает за 12 авеню, получает полную информацию о структуре Парижа и разблокируется достижение \\"Обозреватель Парижа\\"\\nПровал: Игрок получает только базовую информацию о панораме\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Базовые награды:\\n  - Опыт: 800 XP\\n  - Стиль: +15 (базово, может варьироваться в зависимости от выбора)\\n  - Репутация Paris Luxury Community: +15\\n  - Репутация Paris Tourism Community: +10\\n\\nНаграды в зависимости от подхода к шопингу:\\n  - Полноценный шопинг: -10 000 еддис, стиль +20, разблокируется достижение \\"Люксовый шопер\\"\\n  - Экономный маршрут: 0 еддис, стиль +10, разблокируется достижение \\"Окно-шопер\\"\\n  - Сбалансированный подход: -3 000 еддис, стиль +15, разблокируется достижение \\"Умеренный шопер\\"\\n\\nДополнительные награды (при успешных проверках навыков):\\n  - Дополнительная информация о маршруте (при успешной проверке Choice)\\n  - Дополнительная информация о истории Елисейских полей (при успешной проверке Awareness на этапе 1)\\n  - Достижение \\"Собиратель историй\\" (при успешной проверке Social)\\n  - Скидка 10% на покупки (при успешной проверке Shopping)\\n  - Достижение \\"Мастер шопинга\\" (при успешной проверке Shopping)\\n  - +5 XP и достижение \\"Панорамный фотограф\\" (при успешной проверке Photography)\\n  - Достижение \\"Обозреватель Парижа\\" (при успешной проверке Observation)\\n\\nНаграды за темп прогулки:\\n  - Медленный темп: больше информации, достижение \\"Неторопливый исследователь\\"\\n  - Быстрый темп: экономия времени\\n  - Средний темп: баланс информации и времени\\n\\nНаграды за побочные активности:\\n  - Примерка люксового аксессуара: достижение \\"Примерка роскоши\\"\\n  - Фотографирование витрин: достижение \\"Фотограф витрин\\"\\n  - Оба варианта: достижения обоих активностей\\n\\nНаграды за подход к панораме:\\n  - Детальное наблюдение: достижение \\"Панорамный обозреватель\\", полная информация о Париже\\n  - Быстрый осмотр: экономия времени\\n  - Сбалансированный подход: оптимальный баланс\\n\\nУникальные награды:\\n  - Достижение \\"Шопинг на Елисейских\\" (базовое достижение)\\n  - Доступ к информации о люксовых брендах\\n  - Связь с квестами люксового шопинга в других городах (Fifth Avenue в Нью-Йорке, Ginza в Токио)\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест связывается с глобальными сюжетами люксового шопинга в других городах.\\n\\nСвязанные квесты:\\n  - \\"Париж: люксовый шопинг\\" (разблокируется при полноценном шопинге)\\n  - \\"Париж: экономичный маршрут\\" (разблокируется при экономном маршруте)\\n  - \\"Нью-Йорк: Fifth Avenue\\" (связан с люксовым шопингом)\\n  - \\"Токио: Ginza\\" (связан с люксовым шопингом)\\n  - \\"Париж: парад 14 июля\\" (разблокируется после завершения)\\n  - \\"Париж: ночная подсветка\\" (разблокируется после завершения)\\n\\nВлияние на репутацию:\\n  - Paris Luxury Community: +15 (базовая)\\n  - Paris Tourism Community: +10\\n  - Paris Social Community: +5 (при успешных проверках Social)\\n  - Paris Cultural Community: +5 (при успешных проверках Observation)\\n\\nВлияние на стиль:\\n  - Базовый бонус: +15\\n  - Максимальный бонус: +20 (при полноценном шопинге)\\n  - Минимальный бонус: +10 (при экономном маршруте)\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 614, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_director", "changes": "Конверсия квеста «Елисейские Поля» из Markdown в YAML."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлен детальный контент: этапы с локациями и целями (3 этапа), диалоги с NPC (туристический ИИ, продавцы в бутиках, посетитель кафе), проверки навыков (Choice, Awareness, Social, Shopping, Photography, Observation), ветвления и выборы, детальные награды с бонусом стиля до +20."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

