INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-detroit-techno-birthplace', 'Детройт 2020-2029 — Родина техно', 'Игрок знакомится с истоками Detroit techno, посещает ключевые клубы и фестиваль Movement, погружаясь в музыкальную культуру города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-detroit-techno-birthplace", "title": "Детройт 2020-2029 — Родина техно", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-05T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["detroit", "music", "techno"], "topics": ["cultural-heritage", "nightlife"], "related_systems": ["narrative-service", "social-hub-system"], "related_documents": [{"id": "canon-quest-detroit-motown-music", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-340", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/340", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/detroit/2020-2029/quest-005-techno-birthplace.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о техно-сцене Детройта хранился в Markdown и не участвовал в автоматизированных процессах.", "goal": "Структурировать сценарий «Родина техно» для использования в пайплайне концепта и нарратива.", "essence": "Игрок знакомится с истоками Detroit techno, посещает ключевые клубы и фестиваль Movement, погружаясь в музыкальную культуру города.", "key_points": ["Показана история жанра через «Belleville Three» и индустриальный звук.", "Маршрут включает клубы, фестиваль Movement и исследование влияния техно на город.", "Награды усиливают культурную репутацию и дают достижение «Техно Пионер»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Сценарий раскрывает культурное наследие Детройта как родины техно. Игрок изучает истоки жанра, знакомится с легендами сцены и\\nпосещает современные площадки, где музыка продолжает формировать идентичность города.\\n", "mechanics_links": [], "assets": []}, {"id": "itinerary", "title": "Этапы маршрута", "body": "1. Исследование истории Detroit techno и роли «Belleville Three».\\n2. Визит в культовые клубы TV Lounge или Tangent Gallery, знакомство с локальной сценой.\\n3. Участие в Movement Festival и наблюдение за 100-тысячной аудиторией.\\n4. Финальная сцена — осознание связи индустриального звука и души города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Освещены 1980-е годы, ключевые фигуры техно, влияние индустриального шума заводов и глобальное распространение жанра.\\nДополнительно упомянут ежегодный фестиваль Movement как сердце сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards_mechanics", "title": "Награды и механики", "body": "XP: 2000, Валюта: -40, Репутация «Культура»: +25. Игрок получает достижение «Техно Пионер».\\nКлючевые механики — музыкальные события, социальные взаимодействия и историческое погружение в клубную сцену.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 340, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-detroit-techno-birthplace"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Родина техно» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

