-- Quest: canon-quest-paris-2020-2029-montmartre-artists
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-2020-2029-montmartre-artists', 'Париж 2020-2029 — «Художники Монмартра»', 'Квест превращает героя в уличного художника, вводит площадку Place du Tertre и завершает вечерним шоу.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "climb_montmartre", "text": "Подняться на холм Монмартр к базилике Сакре-Кёр", "type": "navigate", "target": "montmartre_hill", "count": 1, "optional": false}, {"id": "rent_artist_spot", "text": "Арендовать место на площади художников Place du Tertre", "type": "interact", "target": "place_du_tertre", "count": 1, "optional": false}, {"id": "draw_portraits", "text": "Нарисовать портреты туристов в мини-игре для заработка", "type": "interact", "target": "portrait_drawing_minigame", "count": 3, "optional": false}, {"id": "visit_moulin_rouge", "text": "Посетить вечернее шоу в Moulin Rouge", "type": "interact", "target": "moulin_rouge_show", "count": 1, "optional": false}]'::jsonb, '{"experience": 1200, "currency": 500, "items": [], "reputation": {"Paris_Art": 15}, "flags": ["montmartre_artist_unlocked", "bohemian_lifestyle"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-2020-2029-montmartre-artists", "title": "Париж 2020-2029 — «Художники Монмартра»", "document_type": "canon", "category": "timeline-author", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["paris", "art", "montmartre"], "topics": ["culture", "lifestyle"], "related_systems": ["narrative-service", "profession-system"], "related_documents": [{"id": "github-issue-616", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/616", "relation": "migrated_to", "migrated_at": "2025-11-22T23:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/paris/2020-2029/quest-006-montmartre-artists.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватает квеста, демонстрирующего богемную культуру Монмартра и карьеру художника.", "goal": "Познакомить игрока с артистической жизнью холма, мини-игрой рисования и социальными контактами.", "essence": "Квест превращает героя в уличного художника, вводит площадку Place du Tertre и завершает вечерним шоу.", "key_points": ["Подъём к базилике Сакре-Кёр и аренда места на площади художников.", "Мини-игра рисования портретов туристов для заработка.", "Вечер в Moulin Rouge закрепляет богемную атмосферу."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "climb_montmartre", "text": "Подняться на холм Монмартр к базилике Сакре-Кёр", "type": "navigate", "target": "montmartre_hill", "count": 1, "optional": false}, {"id": "rent_artist_spot", "text": "Арендовать место на площади художников Place du Tertre", "type": "interact", "target": "place_du_tertre", "count": 1, "optional": false}, {"id": "draw_portraits", "text": "Нарисовать портреты туристов в мини-игре для заработка", "type": "interact", "target": "portrait_drawing_minigame", "count": 3, "optional": false}, {"id": "visit_moulin_rouge", "text": "Посетить вечернее шоу в Moulin Rouge", "type": "interact", "target": "moulin_rouge_show", "count": 1, "optional": false}], "rewards": {"experience": 1200, "currency": 500, "items": [], "reputation": {"Paris_Art": 15}, "flags": ["montmartre_artist_unlocked", "bohemian_lifestyle"]}, "branches": []}, "content": {"sections": [{"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Подъём на Монмартр\\nЛокация: Париж, холм Монмартр, лестницы или фуникулёр\\nОписание: Игрок поднимается по лестницам или использует фуникулёр, получая вид на Париж и знакомясь с историей холма.\\nЦели:\\n  - Прибыть к подножию холма Монмартр\\n  - Выбрать способ подъёма (лестницы или фуникулёр)\\n  - Подняться на холм Монмартр\\n  - Полюбоваться видом на Париж с вершины холма\\n  - Изучить историю Монмартра\\n  - Дойти до базилики Сакре-Кёр\\n  - Проверка навыков: Awareness 0.3+ для изучения истории, Perception 0.3+ для наблюдений\\n\\nЭтап 2: Площадь художников\\nЛокация: Париж, Place du Tertre\\nОписание: На Place du Tertre герой арендует мольберт и запускает мини-игру рисования портретов туристов, зарабатывая 1 000–5 000 еддиков.\\nЦели:\\n  - Прибыть на Place du Tertre (площадь художников)\\n  - Арендовать место для работы\\n  - Арендовать мольберт и материалы\\n  - Найти клиентов-туристов для портретов\\n  - Нарисовать портреты туристов\\n  - Заработать деньги от продажи портретов (1 000–5 000 еддиков)\\n  - Проверка навыков: Art 0.4+ для рисования портретов, Charisma 0.3+ для привлечения клиентов\\n\\nЭтап 3: Богемные знакомства\\nЛокация: Париж, Place du Tertre и окрестности Монмартра\\nОписание: Игрок взаимодействует с художниками, поэтами и музыкантами, открывая социальные связи и квестовую карьеру «Художник Монмартра».\\nЦели:\\n  - Встретиться с местными художниками\\n  - Взаимодействовать с поэтами на площади\\n  - Взаимодействовать с музыкантами\\n  - Установить социальные связи с богемой Монмартра\\n  - Узнать о богемной жизни холма\\n  - Открыть карьеру \\"Художник Монмартра\\"\\n  - Проверка навыков: Social 0.3+ для взаимодействия, Networking 0.3+ для установления связей\\n\\nЭтап 4: Вечер в Moulin Rouge\\nЛокация: Париж, кабаре Moulin Rouge\\nОписание: Завершение проходит в кабаре; игрок выбирает, продолжить ли богемную линию или остаться наблюдателем.\\nЦели:\\n  - Прибыть в кабаре Moulin Rouge\\n  - Принять участие в вечернем шоу\\n  - Наблюдать за выступлением артистов\\n  - Взаимодействовать с богемой в кабаре\\n  - Принять решение о будущем в богемной жизни\\n  - Завершить квест\\n  - Проверка навыков: Social 0.3+ для взаимодействия, Choice 0.3+ для решения\\n", "mechanics_links": ["mechanics/minigames/live-painting.yaml", "mechanics/social/networking.yaml"], "assets": []}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Художник (Пьер Мартин)\\nДиалог 1 (начальный):\\n  - Пьер: \\"Bonjour! Добро пожаловать на Монмартр! Вы тоже художник?\\"\\n  - Игрок: \\"Да, я хочу попробовать свои силы.\\"\\n  - Пьер: \\"Отлично! Монмартр — это место, где рождается искусство. Здесь работали Пикассо, Ван Гог, Модильяни...\\"\\n  - Игрок: \\"Расскажите о Place du Tertre.\\"\\n  - Пьер: \\"Place du Tertre — это площадь художников. Здесь можно арендовать место, рисовать портреты туристов и зарабатывать на жизнь. Это не просто работа — это образ жизни!\\"\\n\\nАрендодатель места (Мари Дюбуа)\\nДиалог 2:\\n  - Мари: \\"Вы хотите арендовать место для работы? У нас есть свободные места на площади.\\"\\n  - Игрок: \\"Сколько стоит?\\"\\n  - Мари: \\"Аренда места стоит 50 еддис в день. Это включает мольберт и базовые материалы. Дополнительные материалы можно купить отдельно.\\"\\n  - Игрок: \\"А что если я не продам ни одной картины?\\"\\n  - Мари: \\"Это риск, но если вы будете активны и общительны, то найдёте клиентов. Главное — верить в себя и своё искусство!\\"\\n\\nПоэт (Жан-Люк)\\nДиалог 3:\\n  - Жан-Люк: \\"Привет! Я поэт. Монмартр — это место, где искусство встречается с жизнью. Ты тоже творишь?\\"\\n  - Игрок: \\"Да, я рисую портреты.\\"\\n  - Жан-Люк: \\"Отлично! Искусство связывает людей. Здесь на площади встречаются художники, поэты, музыканты... Это сообщество творческих людей.\\"\\n  - Игрок: \\"Что особенного в Монмартре?\\"\\n  - Жан-Люк: \\"Монмартр — это душа Парижа. Здесь живёт богема, здесь рождается новое искусство. Это место, где прошлое встречается с настоящим.\\"\\n\\nМузыкант (Софи Бернар)\\nДиалог 4:\\n  - Софи: \\"Bonjour! Я играю здесь на гитаре. Монмартр — это место, где музыка оживает.\\"\\n  - Игрок: \\"Что вы играете?\\"\\n  - Софи: \\"Французский шансон, классические композиции... Музыка помогает людям чувствовать. А вы художник?\\"\\n  - Игрок: \\"Да, я рисую портреты туристов.\\"\\n  - Софи: \\"Замечательно! Искусство — это способ общения. Мы все создаём что-то прекрасное и делимся этим с миром.\\"\\n\\nПосетитель кабаре (Анри)\\nДиалог 5:\\n  - Анри: \\"Добро пожаловать в Moulin Rouge! Это легендарное кабаре Монмартра. Здесь собирается богема Парижа.\\"\\n  - Игрок: \\"Расскажите о кабаре.\\"\\n  - Анри: \\"Moulin Rouge существует уже более века. Здесь выступают лучшие артисты, танцоры, музыканты. Это место, где искусство встречается с развлечением.\\"\\n  - Игрок: \\"Что здесь происходит?\\"\\n  - Анри: \\"Каждый вечер — это шоу. Танцы, музыка, атмосфера богемы. Это место, где можно отдохнуть и насладиться искусством.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Способ подъёма на Монмартр\\nУсловие: При подъёме на холм\\nВарианты:\\n  - Лестницы: более медленно, но можно больше увидеть и изучить, разблокируется дополнительная информация о истории\\n  - Фуникулёр: быстрее, удобнее, экономия времени, но меньше информации\\n  - Смешанный подход: подняться на фуникулёре, спуститься по лестницам, оптимальный баланс\\n\\nВыбор 2: Подход к рисованию\\nУсловие: При работе на площади\\nВарианты:\\n  - Фокус на качестве: рисовать качественные портреты, меньше клиентов, но выше цена (+3 000–5 000 еддис), разблокируется достижение \\"Мастер портрета\\"\\n  - Фокус на количестве: рисовать много портретов быстро, больше клиентов, но ниже цена (+1 000–3 000 еддис), разблокируется достижение \\"Быстрый художник\\"\\n  - Сбалансированный подход: баланс качества и количества, +2 000–4 000 еддис\\n\\nВыбор 3: Фокус социальных связей\\nУсловие: При взаимодействии с богемой\\nВарианты:\\n  - Больше времени с художниками: установить связи с художниками, разблокируется информация о живописи\\n  - Больше времени с поэтами: установить связи с поэтами, разблокируется информация о литературе\\n  - Больше времени с музыкантами: установить связи с музыкантами, разблокируется информация о музыке\\n  - Сбалансированный подход: установить связи со всеми группами, получить информацию о всех видах искусства\\n\\nВыбор 4: Финальное решение о богемной линии (критический)\\nУсловие: В кабаре Moulin Rouge\\nВарианты:\\n  - Продолжить богемную линию: посвятить себя жизни художника, разблокируется квест \\"Париж: карьера художника\\", разблокируется достижение \\"Богемный артист\\", +10 репутация Bohemian Community\\n  - Остаться наблюдателем: продолжить как хобби, разблокируется квест \\"Париж: хобби художника\\", +5 репутация Bohemian Community\\n  - Отказаться от богемной жизни: просто завершить квест, получить только базовые награды\\n", "mechanics_links": [], "assets": []}, {"id": "skill_checks", "title": "Проверки навыков", "body": "Проверка 1: Awareness (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок глубоко изучает историю Монмартра, разблокируется дополнительная информация о холме и его значении\\nПровал: Игрок получает только базовую информацию\\n\\nПроверка 1b: Perception (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок внимательно наблюдает за видом на Париж, разблокируется дополнительная информация о достопримечательностях\\nПровал: Игрок получает только общее представление о виде\\n\\nПроверка 2: Art (Этап 2)\\nТребование: 0.4+\\nУспех: Игрок успешно рисует портреты, получает больше денег (до +5 000 еддис) и разблокируется достижение \\"Мастер портрета\\" и +10 XP\\nПровал: Игрок рисует стандартно, получает базовый доход (1 000–3 000 еддис)\\n\\nПроверка 2b: Charisma (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок успешно привлекает клиентов, увеличивает количество заказов на 30% и разблокируется достижение \\"Харизматичный художник\\"\\nПровал: Игрок привлекает стандартное количество клиентов\\n\\nПроверка 3: Social (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок успешно взаимодействует с богемой, устанавливает больше связей и разблокируется достижение \\"Богемный знакомый\\"\\nПровал: Игрок получает только базовые связи\\n\\nПроверка 3b: Networking (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок успешно устанавливает социальные связи, разблокируется дополнительная информация о карьере художника и разблокируется достижение \\"Сетевой мастер\\"\\nПровал: Игрок получает только стандартные связи\\n\\nПроверка 4: Social (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно взаимодействует с богемой в кабаре, получает дополнительные контакты и разблокируется достижение \\"Богемный общительный\\" и +5 XP\\nПровал: Игрок получает стандартное взаимодействие\\n\\nПроверка 4b: Choice (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок делает правильный выбор для финальной сцены, получает дополнительные бонусы и разблокируется достижение \\"Мудрый выбор\\"\\nПровал: Игрок получает стандартные результаты выбора\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Базовые награды:\\n  - Опыт: 2 000 XP\\n  - Репутация \\"Богемный артист\\": +5 (базово, может варьироваться в зависимости от выбора)\\n  - Репутация Paris Art Community: +15\\n  - Репутация Bohemian Community: +5/+10 (в зависимости от выбора)\\n\\nНаграды в зависимости от финального выбора:\\n  - Продолжить богемную линию: разблокируется квест \\"Париж: карьера художника\\", достижение \\"Богемный артист\\", +10 репутация Bohemian Community\\n  - Остаться наблюдателем: разблокируется квест \\"Париж: хобби художника\\", +5 репутация Bohemian Community\\n  - Отказаться от богемной жизни: только базовые награды\\n\\nДоход с продаж картин:\\n  - Базовый доход: 1 000–3 000 еддис (при стандартном подходе)\\n  - Доход при фокусе на качестве: +3 000–5 000 еддис\\n  - Доход при фокусе на количестве: +1 000–3 000 еддис\\n  - Доход при сбалансированном подходе: +2 000–4 000 еддис\\n  - Доход при успешной проверке Art: до +5 000 еддис\\n  - Доход при успешной проверке Charisma: увеличение на 30%\\n\\nДополнительные награды (при успешных проверках навыков):\\n  - Дополнительная информация о истории Монмартра (при успешной проверке Awareness)\\n  - Дополнительная информация о достопримечательностях (при успешной проверке Perception)\\n  - +10 XP и достижение \\"Мастер портрета\\" (при успешной проверке Art)\\n  - Доход до +5 000 еддис (при успешной проверке Art)\\n  - Достижение \\"Харизматичный художник\\" (при успешной проверке Charisma)\\n  - Увеличение количества заказов на 30% (при успешной проверке Charisma)\\n  - Достижение \\"Богемный знакомый\\" (при успешной проверке Social на этапе 3)\\n  - Достижение \\"Сетевой мастер\\" (при успешной проверке Networking)\\n  - +5 XP и достижение \\"Богемный общительный\\" (при успешной проверке Social на этапе 4)\\n  - Достижение \\"Мудрый выбор\\" (при успешной проверке Choice)\\n\\nНаграды за способ подъёма:\\n  - Лестницы: дополнительная информация о истории, достижение \\"Исследователь лестниц\\"\\n  - Фуникулёр: экономия времени\\n  - Смешанный подход: оптимальный баланс\\n\\nНаграды за подход к рисованию:\\n  - Фокус на качестве: достижение \\"Мастер портрета\\", доход +3 000–5 000 еддис\\n  - Фокус на количестве: достижение \\"Быстрый художник\\", доход +1 000–3 000 еддис\\n  - Сбалансированный подход: доход +2 000–4 000 еддис\\n\\nНаграды за фокус социальных связей:\\n  - Больше времени с художниками: информация о живописи, достижение \\"Знаток живописи\\"\\n  - Больше времени с поэтами: информация о литературе, достижение \\"Знаток литературы\\"\\n  - Больше времени с музыкантами: информация о музыке, достижение \\"Знаток музыки\\"\\n  - Сбалансированный подход: информация о всех видах искусства\\n\\nУникальные