-- Quest: canon-quest-paris-2029-008-versailles
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-2029-008-versailles', 'Париж: Версальский дворец', 'Игрок посещает залы, наблюдает зеркальную галерею и участвует в виртуальном балу XVII века.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "travel_versailles", "text": "Добраться до Версальского дворца", "type": "navigate", "target": "versailles_palace", "count": 1, "optional": false}, {"id": "tour_palace", "text": "Пройти экскурсию по залам дворца и зеркальной галерее", "type": "interact", "target": "versailles_palace_tour", "count": 1, "optional": false}, {"id": "explore_gardens", "text": "Исследовать геометрические сады Версаля", "type": "navigate", "target": "versailles_gardens", "count": 1, "optional": false}, {"id": "vr_ball", "text": "Участвовать в VR-погружении в бал Людовика XIV", "type": "interact", "target": "versailles_vr_ball", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "currency": 0, "items": [{"item_id": "achievement_visit_sun_king", "quantity": 1}], "reputation": {"France_Culture": 20}, "flags": ["versailles_visited", "cultural_rating_increased"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-2029-008-versailles", "title": "Париж: Версальский дворец", "document_type": "canon", "category": "quests", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["paris", "versailles", "royal-france"], "topics": ["cultural-tourism", "immersive-history"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}, {"id": "github-issue-618", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/618", "relation": "migrated_to", "migrated_at": "2025-11-22T23:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/paris/2020-2029/quest-008-versailles-palace.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо представить Версаль как культурный центр с VR-реконструкцией балов Людовика XIV.", "goal": "Сформировать квест, объединяющий экскурсию по дворцу, садам и иммерсивный опыт двора «Короля-Солнца».", "essence": "Игрок посещает залы, наблюдает зеркальную галерею и участвует в виртуальном балу XVII века.", "key_points": ["Подчёркнуты роскошь интерьеров и геометрические сады.", "Есть этап VR-погружения в бал Людовика XIV.", "Награды дают культурный рейтинг и ачивку «Визит к Королю-Солнцу»."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "travel_versailles", "text": "Добраться до Версальского дворца", "type": "navigate", "target": "versailles_palace", "count": 1, "optional": false}, {"id": "tour_palace", "text": "Пройти экскурсию по залам дворца и зеркальной галерее", "type": "interact", "target": "versailles_palace_tour", "count": 1, "optional": false}, {"id": "explore_gardens", "text": "Исследовать геометрические сады Версаля", "type": "navigate", "target": "versailles_gardens", "count": 1, "optional": false}, {"id": "vr_ball", "text": "Участвовать в VR-погружении в бал Людовика XIV", "type": "interact", "target": "versailles_vr_ball", "count": 1, "optional": false}], "rewards": {"experience": 1500, "currency": 0, "items": [{"item_id": "achievement_visit_sun_king", "quantity": 1}], "reputation": {"France_Culture": 20}, "flags": ["versailles_visited", "cultural_rating_increased"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест PARIS-2029-008 — культурная поездка в Версаль. Игрок добирается до дворца, проходит экскурсию и завершается\\nиммерсивным балом через VR-инсталляцию.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Поездка в Версаль\\nЛокация: Париж → Версаль, транспорт, дворец Версаль, вход\\nОписание: Игрок добирается до дворца Версаль из Парижа и регистрируется на экскурсию.\\nЦели:\\n  - Отправиться из Парижа в Версаль (транспорт)\\n  - Прибыть к дворцу Версаль\\n  - Пройти регистрацию на экскурсию\\n  - Изучить историю Версаля (дворец Короля-Солнца Людовика XIV)\\n  - Получить билеты на экскурсию и VR-сеанс\\n  - Войти в дворец\\n  - Проверка навыков: Planning 0.3+ для планирования поездки, Awareness 0.3+ для изучения истории\\n\\nЭтап 2: Экскурсия по дворцу\\nЛокация: Версаль, королевские апартаменты, Зеркальная галерея, зал сражений\\nОписание: Осмотр королевских апартаментов, Зеркальной галереи и зала сражений. Дворец насыщен золотом, зеркалами и символами абсолютизма.\\nЦели:\\n  - Осмотреть королевские апартаменты\\n  - Посетить Зеркальную галерею (67 метров, 357 зеркал)\\n  - Изучить зал сражений\\n  - Взаимодействовать с интерактивными экспонатами\\n  - Изучить историю абсолютизма и роскоши двора\\n  - Наблюдать за интерьерами (золото, зеркала, символы королевской власти)\\n  - Проверка навыков: History 0.4+ для изучения истории, Observation 0.3+ для наблюдения за интерьерами\\n\\nЭтап 3: Прогулка по садам\\nЛокация: Версаль, сады, фонтаны, geometric art installations\\nОписание: Прогулка по садам, активация музыкальных фонтанов и geometric art installations. Сады спроектированы Андре Ленотром и сопровождаются музыкой Люлли.\\nЦели:\\n  - Прогуляться по садам Версаля\\n  - Активировать музыкальные фонтаны\\n  - Взаимодействовать с geometric art installations\\n  - Изучить геометрию садов (симметрия, порядок, контроль природы)\\n  - Насладиться музыкой Люлли\\n  - Сделать фотографии садов\\n  - Проверка навыков: Appreciation 0.3+ для наслаждения искусством, Photography 0.3+ для фотографий\\n\\nЭтап 4: VR-реконструкция бала Людовика XIV\\nЛокация: Версаль, VR-инсталляция\\nОписание: Запуск VR-реконструкции бала Людовика XIV и участие в танце. Иммерсивный опыт двора \\"Короля-Солнца\\".\\nЦели:\\n  - Запустить VR-реконструкцию бала\\n  - Погрузиться в виртуальный бал XVII века\\n  - Взаимодействовать с историческими персонажами (Людовик XIV, придворные)\\n  - Принять участие в танце (менуэт, гавот)\\n  - Изучить придворный этикет и манеры\\n  - Пережить опыт бала Короля-Солнца\\n  - Проверка навыков: Social 0.3+ для взаимодействия, Dance 0.3+ для танца, Etiquette 0.3+ для придворного этикета\\n\\nЭтап 5: Сбор полевых записей\\nЛокация: Версаль, архив культурного наследия\\nОписание: Сбор полевых записей для архива культурного наследия. Документирование опыта визита в Версаль.\\nЦели:\\n  - Собрать полевые записи о визите\\n  - Документировать опыт экскурсии\\n  - Записать впечатления о VR-бале\\n  - Создать записи для архива культурного наследия\\n  - Завершить квест\\n  - Проверка навыков: Documentation 0.3+ для создания записей, Writing 0.3+ для написания отчетов\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Дворец насыщен золотом, зеркалами и символами абсолютизма.\\n- Сады спроектированы Андре Ленотром и сопровождаются музыкой Люлли.\\n- Версаль считается «величайшим дворцом Европы», задавшим моду другим монархиям.\\n", "mechanics_links": [], "assets": []}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Гид (Мари Дюбуа)\\nДиалог 1 (начальный):\\n  - Мари: \\"Bonjour! Добро пожаловать в Версаль! Вы готовы к экскурсии?\\"\\n  - Игрок: \\"Да, я готов.\\"\\n  - Мари: \\"Отлично! Версаль — это дворец Короля-Солнца Людовика XIV. Здесь вы увидите роскошь абсолютизма и величие французской монархии.\\"\\n  - Игрок: \\"Расскажите о дворце.\\"\\n  - Мари: \\"Версаль считается величайшим дворцом Европы. Дворец насыщен золотом, зеркалами и символами королевской власти. Здесь жил Король-Солнце и его придворные.\\"\\n\\nИсторик (Доктор Пьер Мартин)\\nДиалог 2 (в Зеркальной галерее):\\n  - Доктор Мартин: \\"Вы находитесь в Зеркальной галерее — самом известном зале Версаля. 67 метров в длину, 357 зеркал...\\"\\n  - Игрок: \\"Почему это так важно?\\"\\n  - Доктор Мартин: \\"Зеркальная галерея символизирует абсолютизм. Зеркала отражают свет и создают иллюзию бесконечности — метафора безграничной власти короля.\\"\\n  - Игрок: \\"А что о балах?\\"\\n  - Доктор Мартин: \\"Здесь проходили самые роскошные балы Европы. Людовик XIV устраивал великолепные празднества, чтобы продемонстрировать своё могущество.\\"\\n\\nVR-оператор (Люк Бернар)\\nДиалог 3 (перед VR-сеансом):\\n  - Люк: \\"Готовы к VR-реконструкции бала Людовика XIV? Это уникальный опыт — вы окажетесь на балу XVII века.\\"\\n  - Игрок: \\"Да, я готов.\\"\\n  - Люк: \\"VR-реконструкция полностью погрузит вас в эпоху Короля-Солнца. Вы встретите исторических персонажей, научитесь танцевать менуэт и узнаете придворный этикет.\\"\\n  - Игрок: \\"Что я увижу?\\"\\n  - Люк: \\"Вы увидите бал во всей его роскоши: музыку Люлли, танцы, блеск золота. Это путешествие во времени.\\"\\n\\nПосетитель (Анри)\\nДиалог 4 (в садах):\\n  - Анри: \\"Версальские сады — это произведение искусства! Геометрические формы, симметрия, порядок...\\"\\n  - Игрок: \\"Кто создал сады?\\"\\n  - Анри: \\"Сады спроектированы Андре Ленотром — великим садовником. Он создал геометрические формы, которые символизируют контроль человека над природой. Это метафора абсолютизма.\\"\\n  - Игрок: \\"А музыка?\\"\\n  - Анри: \\"Музыка Люлли сопровождает фонтаны. Это создаёт атмосферу балов Короля-Солнца. Музыка оживляет сады.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Способ поездки в Версаль\\nУсловие: При планировании поездки\\nВарианты:\\n  - Поезд: быстро и удобно, стандартная стоимость (-25 еддис)\\n  - Автомобиль: свобода, но дороже (-40 еддис)\\n  - Экскурсионный автобус: с гидом, включает информацию, дешевле (-30 еддис)\\n\\nВыбор 2: Фокус экскурсии по дворцу\\nУсловие: При осмотре дворца\\nВарианты:\\n  - Фокус на истории: детальное изучение истории, больше информации, разблокируется достижение \\"Знаток истории\\"\\n  - Фокус на искусстве: изучение интерьеров и искусства, больше впечатлений, разблокируется достижение \\"Ценитель искусства\\"\\n  - Сбалансированный подход: баланс истории и искусства\\n\\nВыбор 3: Подход к прогулке по садам\\nУсловие: При прогулке по садам\\nВарианты:\\n  - Активная прогулка: быстро обойти все сады, экономия времени\\n  - Медленная прогулка: тщательно изучить сады, больше впечатлений, разблокируется дополнительная информация\\n  - Сбалансированный подход: оптимальный баланс\\n\\nВыбор 4: Уровень участия в VR-бале (критический)\\nУсловие: В VR-реконструкции бала\\nВарианты:\\n  - Активное участие: полностью погрузиться в бал, участвовать в танцах, разблокируется достижение \\"Танцор двора\\", +5 XP\\n  - Наблюдение: наблюдать за балом, меньше участия, но больше информации\\n  - Сбалансированный подход: умеренное участие, баланс опыта и информации\\n", "mechanics_links": [], "assets": []}, {"id": "skill_checks", "title": "Проверки навыков", "body": "Проверка 1: Planning (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок эффективно планирует поездку, экономит время и деньги, разблокируется достижение \\"Планировщик\\"\\nПровал: Игрок планирует стандартно\\n\\nПроверка 1b: Awareness (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок глубоко изучает историю Версаля, разблокируется дополнительная информация о дворце\\nПровал: Игрок получает только базовую информацию\\n\\nПроверка 2: History (Этап 2)\\nТребование: 0.4+\\nУспех: Игрок успешно изучает историю абсолютизма, разблокируется достижение \\"Знаток истории\\" и +5 XP\\nПровал: Игрок получает только базовое понимание истории\\n\\nПроверка 2b: Observation (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок внимательно наблюдает за интерьерами, разблокируется дополнительная информация о роскоши и разблокируется достижение \\"Наблюдатель\\"\\nПровал: Игрок получает только общее впечатление\\n\\nПроверка 3: Appreciation (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок глубоко наслаждается искусством садов, разблокируется достижение \\"Ценитель искусства\\" и +5 XP\\nПровал: Игрок получает стандартное впечатление\\n\\nПроверка 3b: Photography (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок делает качественные фотографии садов, разблокируется достижение \\"Фотограф\\" и предмет \\"Фото садов Версаля\\"\\nПровал: Игрок делает стандартные фотографии\\n\\nПроверка 4: Social (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно взаимодействует с историческими персонажами в VR, разблокируется достижение \\"Социальный\\" и +5 XP\\nПровал: Игрок получает стандартное взаимодействие\\n\\nПроверка 4b: Dance (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно танцует менуэт, разблокируется достижение \\"Танцор двора\\" и +5 XP\\nПровал: Игрок танцует стандартно\\n\\nПроверка 4c: Etiquette (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно изучает придворный этикет, разблокируется достижение \\"Этикет\\" и +5 XP\\nПровал: Игрок получает только базовое понимание этикета\\n\\nПроверка 5: Documentation (Этап 5)\\nТребование: 0.3+\\nУспех: Игрок создаёт качественные полевые записи, разблокируется достижение \\"Документалист\\" и +5 XP\\nПровал: Игрок создаёт стандартные записи\\n\\nПроверка 5b: Writing (Этап 5)\\nТребование: 0.3+\\nУспех: Игрок создаёт подробный отчёт о визите, разблокируется достижение \\"Писатель\\" и +5 XP\\nПровал: Игрок создаёт базовый отчёт\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Базовые награды:\\n  - Опыт: 2 000 XP\\n  - Культура: +15\\n  - Репутация Paris Cultural Community: +20\\n  - Репутация Versailles Heritage: +15\\n  - Валюта: -50 еддис (билеты и VR-сеанс, может варьироваться в зависимости от способа поездки)\\n\\nНаграды в зависимости от уровня участия в VR-бале:\\n  - Активное участие: достижение \\"Танцор двора\\", +5 XP, дополнительная информация о придворной жизни\\n  - Наблюдение: больше информации о истории балов, дополнительная информация о этикете\\n  - Сбалансированный подход: баланс опыта и информации\\n\\nДополнительные награды (при успешных проверках навыков):\\n  - Достижение \\"Планировщик\\" (при успешной проверке Planning)\\n  - Экономия времени и денег (при успешной проверке Planning)\\n  - Дополнительная информация о Версале (при успешной проверке Awareness)\\n  - Достижение \\"Знаток истории\\" (при успешной проверке History)\\n  - +5 XP (при успешной проверке History)\\n  - Достижение \\"Наблюдатель\\" (при успешной проверке Observation)\\n  - Дополнительная информация о роскоши (при успешной проверке Observation)\\n  - Достижение \\"Ценитель искусства\\" (при успешной проверке Appreciation)\\n  - +5 XP (при успешной проверке Appreciation)\\n  - Достижение \\"Фотограф\\" (при успешной проверке Photography)\\n  - Предмет \\"Фото садов Версаля\\" (при успешной проверке Photography)\\n  - Достижение \\"Социальный\\" (при успешной проверке Social)\\n  - +5 XP (при успешной проверке Social)\\n  - Достижение \\"Танцор двора\\" (при успешной проверке Dance)\\n  - +5 XP (при успешной проверке Dance)\\n  - Достижение \\"Этикет\\" (при успешной проверке Etiquette)\\n  - +5 XP (при успешной проверке Etiquette)\\n  - Достижение \\"Документалист\\" (при успешной проверке Documentation)\\n  - +5 XP (при успешной проверке Documentation)\\n  - Достижение \\"Писатель\\" (при успешной проверке Writing)\\n  - +5 XP (при успешной проверке Writing)\\n\\nНаграды за способ поездки:\\n  - Поезд: стандартная стоимость (-25 еддис)\\n  - Автомобиль: свобода, но дороже (-40 еддис)\\n  - Экскурсионный автобус: включает информацию, дешевле (-30 еддис)\\n\\nНаграды за фокус экскурсии:\\n  - Фокус на истории: достижение \\"Знаток истории\\", больше информации\\n  - Фокус на искусстве: достижение \\"Ценитель искусства\\", больше впечатлений\\n  - Сбалансированный подход: баланс\\n\\nНаграды за подход к прогулке:\\n  - Активная прогулка: экономия времени\\n  - Медленная прогулка: дополнительная информация, больше впечатлений\\n  - Сбалансированный подход: оптимальный баланс\\n\\nУникальные награды:\\n  - Ачивка \\"Визит к Королю-Солнцу\\" (базовая)\\n  - Доступ к VR-реконструкциям других исторических событий (при успешном VR-опыте)\\n  - Полевые записи для архива культурного наследия (при создании записей)\\n  - Влияние на все последующие культурные квесты в Париже\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристическая экскурсия с интерактивными экспонатами.\\n- VR-балы с историческими персонажами.\\n- Сбор культурных маркеров для будущих дворцовых сценариев.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает линию имперских дворцов и сопоставляется с сюжетами Санкт-Петербурга и Пекина.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 618, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест визита в Версаль."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлен детальный контент: этапы с локациями и целями (5 этапов), диалоги с NPC (гид, историк, VR-оператор, посетитель), проверки навыков (Planning, Awareness, History, Observation, Appreciation, P