INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-rome-2029-005-roman-empire-vr', 'Рим: VR-погружение в Римскую империю', 'Игрок надевает шлем, перемещается по виртуальному городу, общается с NPC и завершает встречей с императором.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "activate_vr_complex", "text": "Активировать VR-комплекс и пройти калибровку сенсорных интерфейсов", "type": "interact", "target": "vr_museum_complex", "count": 1, "optional": false}, {"id": "explore_forum_romanum", "text": "Появиться на Форуме Романуме и ознакомиться с политическим центром", "type": "explore", "target": "forum_romanum_vr", "count": 1, "optional": false}, {"id": "visit_landmarks", "text": "Посетить Колизей, Пантеон, термы Каракаллы и Цирк Максимус", "type": "explore", "target": "ancient_rome_landmarks", "count": 4, "optional": false}, {"id": "interact_with_npcs", "text": "Взаимодействовать с NPC-легионерами и гражданами, изучая повседневные практики", "type": "interact", "target": "ancient_rome_npcs", "count": 5, "optional": false}, {"id": "meet_emperor", "text": "Завершить аудиенцией у Августа и вернуться в современность", "type": "interact", "target": "emperor_audience_vr", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "currency": -50, "items": [], "reputation": {"rome_cultural": 20}, "flags": ["citizen_of_rome_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-rome-2029-005-roman-empire-vr", "title": "Рим: VR-погружение в Римскую империю", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["rome", "vr", "history"], "topics": ["educational-experience", "immersive-storytelling"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/rome/2020-2029/quest-005-roman-empire-vr.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно было отразить образовательное VR-путешествие по Риму I века н.э.", "goal": "Позволить игрокам шагнуть в Pax Romana, посетить ключевые локации и почувствовать масштаб империи.", "essence": "Игрок надевает шлем, перемещается по виртуальному городу, общается с NPC и завершает встречей с императором.", "key_points": [{"Зафиксированы основные достопримечательности": "форум, Колизей, Пантеон, термы и Цирк Максимус."}, "Подчёркнуты визуальные детали мрамора, тоги, латинская речь и марширующие легионы.", "Награды дают культурный опыт и ачивку «Гражданин Рима»."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "activate_vr_complex", "text": "Активировать VR-комплекс и пройти калибровку сенсорных интерфейсов", "type": "interact", "target": "vr_museum_complex", "count": 1, "optional": false}, {"id": "explore_forum_romanum", "text": "Появиться на Форуме Романуме и ознакомиться с политическим центром", "type": "explore", "target": "forum_romanum_vr", "count": 1, "optional": false}, {"id": "visit_landmarks", "text": "Посетить Колизей, Пантеон, термы Каракаллы и Цирк Максимус", "type": "explore", "target": "ancient_rome_landmarks", "count": 4, "optional": false}, {"id": "interact_with_npcs", "text": "Взаимодействовать с NPC-легионерами и гражданами, изучая повседневные практики", "type": "interact", "target": "ancient_rome_npcs", "count": 5, "optional": false}, {"id": "meet_emperor", "text": "Завершить аудиенцией у Августа и вернуться в современность", "type": "interact", "target": "emperor_audience_vr", "count": 1, "optional": false}], "rewards": {"experience": 3000, "currency": -50, "items": [], "reputation": {"rome_cultural": 20}, "flags": ["citizen_of_rome_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест ROME-2029-005 запускает VR-сессию в музее. Игрок перемещается в античный Рим, проходит экскурсию по знаковым\\nместам и выполняет интерактивные задачи до выхода из симуляции.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Активировать VR-комплекс и пройти калибровку сенсорных интерфейсов.\\n2. Появиться на Форуме Романуме и ознакомиться с политическим центром.\\n3. Посетить Колизей, Пантеон, термы Каракаллы и Цирк Максимус.\\n4. Взаимодействовать с NPC-легионерами и гражданами, изучая повседневные практики.\\n5. Завершить аудиенцией у Августа и вернуться в современность.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Sim используется для реставрации «Roma Aeterna» в белом мраморе.\\n- Звучит разговорная латынь с живым переводом в интерфейсе.\\n- В симуляции подчёркнут «миллион жителей» и структура Pax Romana.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 3 000 XP.\\n- Культурный рейтинг Рима +20.\\n- Валюта: -50 (стоимость VR-сеанса).\\n- Ачивка «Гражданин Рима».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Иммерсивная VR-платформа с тактильной обратной связью.\\n- Обучающие мини-сценарии и тесты по истории.\\n- Реконструкция городских событий с динамическими crowds.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет образовательную арку Рима с глобальными историческими симуляциями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 607, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.1.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлен Issue в начало файла. Структура quest_definition соответствует архитектуре системы квестов (currency, flags, правильная структура rewards)."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики образовательного опыта и иммерсивного повествования."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML VR-квеста по Римской империи."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

