INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-rome-2029-006-mafia-connection', 'Рим: связь с мафией', 'Игрок устанавливает контакт с капо, проходит проверку лояльности, приносит кровную клятву и получает доступ к криминальным контрактам.', 'main', 7, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "meet_capo", "text": "Встретиться с капо в подпольном кафе и доказать полезность семьи", "type": "interact", "target": "underground_cafe_rome", "count": 1, "optional": false}, {"id": "complete_trials", "text": "Выполнить ряд испытаний: сбор долга, устранение свидетеля, доставка запрещённого груза", "type": "perform", "target": "mafia_trials", "count": 3, "optional": false}, {"id": "study_omerta_code", "text": "Подготовиться к церемонии, изучив кодекс омерты и правила поведения", "type": "research", "target": "omerta_codex", "count": 1, "optional": false}, {"id": "blood_oath", "text": "Принять участие в кровной клятве, целуя кольцо дона и подтверждая лояльность", "type": "interact", "target": "mafia_initiation_ceremony", "count": 1, "optional": false}, {"id": "become_made_man", "text": "Получить статус «made man», доступ к мафиозным контрактам и защите семьи", "type": "interact", "target": "made_man_status", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "currency": 20000, "items": [], "reputation": {"camorra": 25, "sacra_corona_unita": 25}, "flags": ["made_man_status", "mafia_protection"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-rome-2029-006-mafia-connection", "title": "Рим: связь с мафией", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["rome", "mafia", "camorra"], "topics": ["criminal-factions", "social-infiltration"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/rome/2020-2029/quest-006-mafia-connection.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В криминальной арке Рима отсутствовал сюжет о проникновении в местную мафию и принятии кодекса омерты.", "goal": "Показать процесс вербовки, испытаний и инициации в структуру Camorra или Sacra Corona Unita.", "essence": "Игрок устанавливает контакт с капо, проходит проверку лояльности, приносит кровную клятву и получает доступ к криминальным контрактам.", "key_points": ["Отражены ритуалы уважения и стиля, присущие итальянской мафии.", "Испытания включают сбор дани и устранение конкурентов.", "Инициация завершается необратимой клятвой омерты и статусом «made man»."]}, "quest_definition": {"quest_type": "main", "level_min": 7, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "meet_capo", "text": "Встретиться с капо в подпольном кафе и доказать полезность семьи", "type": "interact", "target": "underground_cafe_rome", "count": 1, "optional": false}, {"id": "complete_trials", "text": "Выполнить ряд испытаний: сбор долга, устранение свидетеля, доставка запрещённого груза", "type": "perform", "target": "mafia_trials", "count": 3, "optional": false}, {"id": "study_omerta_code", "text": "Подготовиться к церемонии, изучив кодекс омерты и правила поведения", "type": "research", "target": "omerta_codex", "count": 1, "optional": false}, {"id": "blood_oath", "text": "Принять участие в кровной клятве, целуя кольцо дона и подтверждая лояльность", "type": "interact", "target": "mafia_initiation_ceremony", "count": 1, "optional": false}, {"id": "become_made_man", "text": "Получить статус «made man», доступ к мафиозным контрактам и защите семьи", "type": "interact", "target": "made_man_status", "count": 1, "optional": false}], "rewards": {"experience": 5000, "currency": 20000, "items": [], "reputation": {"camorra": 25, "sacra_corona_unita": 25}, "flags": ["made_man_status", "mafia_protection"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест ROME-2029-006 знакомит игрока с криминальными сетями Рима. Нужно завоевать доверие капо, выполнить задания на\\nблаго семьи и пройти обряд принятия в организацию, после чего открываются закрытые фракционные ветки.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Встретиться с капо в подпольном кафе и доказать полезность семьи.\\n2. Выполнить ряд испытаний: сбор долга, устранение свидетеля, доставка запрещённого груза.\\n3. Подготовиться к церемонии, изучив кодекс омерты и правила поведения.\\n4. Принять участие в кровной клятве, целуя кольцо дона и подтверждая лояльность.\\n5. Получить статус «made man», доступ к мафиозным контрактам и защите семьи.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Квест подчёркивает влияние Camorra и Sacra Corona Unita на Рим.\\n- В сценах используются элементы итальянской культуры: эспрессо, amaretti, костюмы кустомизированных портных.\\n- Слушатели слышат термины rispetto, famiglia и традиционные обращения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 5 000 XP.\\n- 20 000 Eddies.\\n- Доступ к мафиозным контрактам и защите семьи.\\n- Фракционный статус, который невозможно отменить без серьёзных последствий.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Фракционная принадлежность с долгосрочными обязательствами.\\n- Стелс и социальные проверки при выполнении заданий.\\n- Система репутации, влияющая на степень доверия и поддержку мафии.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет римскую криминальную арку с глобальными мафиозными сюжетами и открывает кросс-города события.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 608, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.1.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлен Issue в начало файла. Структура quest_definition соответствует архитектуре системы квестов (currency, flags, правильная структура rewards)."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики криминальных фракций и социальной инфильтрации."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML-квест о вступлении в римскую мафию."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

