INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vienna-2029-001-schonbrunn', 'Вена: дворец Шёнбрунн и наследие Габсбургов', 'Игрок исследует дворец, посещает знаковые залы и павильон Gloriette, чтобы прочувствовать масштаб власти Габсбургов.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reach_schonbrunn", "text": "Добраться до дворца Шёнбрунн и получить доступ в туристический центр", "type": "explore", "target": "schonbrunn_palace", "count": 1, "optional": false}, {"id": "visit_halls", "text": "Посетить зеркальный зал, Большую галерею и императорские апартаменты, отмечая интерактивные аудиогиды", "type": "explore", "target": "palace_halls", "count": 3, "optional": false}, {"id": "explore_gardens", "text": "Изучить сад французского образца, включая фонтан Нептуна и лабиринт", "type": "explore", "target": "schonbrunn_gardens", "count": 1, "optional": false}, {"id": "visit_gloriette", "text": "Подняться к павильону Gloriette и выполнить панорамный обзор столицы", "type": "explore", "target": "gloriette_pavilion", "count": 1, "optional": false}, {"id": "complete_tour", "text": "Завершить тур лекцией о политическом влиянии Габсбургов на Европу", "type": "interact", "target": "habsburg_lecture", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "currency": -18, "items": [], "reputation": {"vienna_cultural": 25}, "flags": ["guest_of_emperors_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vienna-2029-001-schonbrunn", "title": "Вена: дворец Шёнбрунн и наследие Габсбургов", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vienna", "schonbrunn", "heritage"], "topics": ["cultural-tourism", "imperial-history"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/vienna/2020-2029/quest-001-schonbrunn-palace.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В линии Вены отсутствовал квест, полностью раскрывающий дворцовый комплекс Шёнбрунн и его барочную роскошь.", "goal": "Зафиксировать исторический тур с акцентом на архитектуру, королевские апартаменты и символику империи.", "essence": "Игрок исследует дворец, посещает знаковые залы и павильон Gloriette, чтобы прочувствовать масштаб власти Габсбургов.", "key_points": ["Маршрут охватывает внешний ансамбль, музейные комнаты и сады в стиле французского барокко.", "Региональный контекст подчёркивает наследие Габсбургов и музыкальные события, связанные с Моцартом.", "Награды ориентированы на культурный прогресс и присвоение титула «Гость Императоров»."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reach_schonbrunn", "text": "Добраться до дворца Шёнбрунн и получить доступ в туристический центр", "type": "explore", "target": "schonbrunn_palace", "count": 1, "optional": false}, {"id": "visit_halls", "text": "Посетить зеркальный зал, Большую галерею и императорские апартаменты, отмечая интерактивные аудиогиды", "type": "explore", "target": "palace_halls", "count": 3, "optional": false}, {"id": "explore_gardens", "text": "Изучить сад французского образца, включая фонтан Нептуна и лабиринт", "type": "explore", "target": "schonbrunn_gardens", "count": 1, "optional": false}, {"id": "visit_gloriette", "text": "Подняться к павильону Gloriette и выполнить панорамный обзор столицы", "type": "explore", "target": "gloriette_pavilion", "count": 1, "optional": false}, {"id": "complete_tour", "text": "Завершить тур лекцией о политическом влиянии Габсбургов на Европу", "type": "interact", "target": "habsburg_lecture", "count": 1, "optional": false}], "rewards": {"experience": 2000, "currency": -18, "items": [], "reputation": {"vienna_cultural": 25}, "flags": ["guest_of_emperors_title"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест VIENNA-2029-001 отправляет игрока в летнюю резиденцию Габсбургов. Нужно пройти экскурсию по ключевым залам,\\nизучить садово-парковый комплекс и завершить маршрут на павильоне Gloriette с панорамой Вены.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Добраться до дворца Шёнбрунн и получить доступ в туристический центр.\\n2. Посетить зеркальный зал, Большую галерею и императорские апартаменты, отмечая интерактивные аудиогиды.\\n3. Изучить сад французского образца, включая фонтан Нептуна и лабиринт.\\n4. Подняться к павильону Gloriette и выполнить панорамный обзор столицы.\\n5. Завершить тур лекцией о политическом влиянии Габсбургов на Европу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Комплекс включает 1 441 комнату и сравнивается с Версалем.\\n- Моцарт выступал в зеркальном зале, когда ему было шесть лет.\\n- Резиденция занесена в список всемирного наследия UNESCO.\\n- Отсылки к 640-летнему правлению династии подчёркивают масштаб империи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 000 XP.\\n- Культурный рейтинг Вены +25.\\n- Валюта: -18 (стоимость экскурсии и сувениров).\\n- Титул «Гость Императоров».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Исторический туризм и изучение наследия империй.\\n- Визуальный осмотр архитектурных объектов с голографическими примечаниями.\\n- Сбор культурных маркеров для долгосрочных сюжетных линий.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает культурную арку Вены и открывает сюжетные точки про Габсбургов и их влияние на современную Европу.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 593, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.1.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлен Issue в начало файла. Структура quest_definition соответствует архитектуре системы квестов (currency, flags, правильная структура rewards)."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики культурного туризма и имперской истории."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Создан YAML для квеста «Дворец Шёнбрунн» с маршрутами и наградами."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

