INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vienna-2029-003-sachertorte', 'Вена: легенда торта Захер', 'Игрок посещает Café Sacher, узнаёт о рецепте 1832 года и получает временный бафф от десерта.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reserve_table", "text": "Зарезервировать столик в Café Sacher", "type": "interact", "target": "cafe_sacher", "count": 1, "optional": false}, {"id": "learn_recipe_history", "text": "Пообщаться с персоналом и узнать о происхождении рецепта 1832 года", "type": "interact", "target": "sacher_staff", "count": 1, "optional": false}, {"id": "order_sachertorte", "text": "Заказать оригинальный Sachertorte с обязательным абрикосовым джемом и сливками", "type": "consume", "target": "sachertorte_dessert", "count": 1, "optional": false}, {"id": "compare_demel_recipe", "text": "Сравнить альтернативный рецепт Demel через исторические заметки", "type": "research", "target": "demel_recipe_notes", "count": 1, "optional": false}, {"id": "complete_tasting", "text": "Завершить дегустацию отзывом и получить сувенирную упаковку", "type": "interact", "target": "tasting_completion", "count": 1, "optional": false}]'::jsonb, '{"experience": 800, "currency": -12, "items": [{"item_id": "sugar_boost_buff", "name": "Бафф «Сахарный подъём»: +15% Energy на 4 часа", "quantity": 1}, {"item_id": "sacher_gourmet_title", "name": "Титул «Захер Гурман»", "quantity": 1}], "reputation": {}, "flags": ["sacher_gourmet_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vienna-2029-003-sachertorte", "title": "Вена: легенда торта Захер", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vienna", "sachertorte", "culinary"], "topics": ["gastronomy", "luxury-experience"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-region-europe-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/vienna/2020-2029/quest-003-sachertorte.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало короткого квеста, демонстрирующего культовый десерт Вены и роскошную атмосферу Hotel Sacher.", "goal": "Показать гастрономическую традицию через дегустацию оригинального Sachertorte и историю соперничества с Demel.", "essence": "Игрок посещает Café Sacher, узнаёт о рецепте 1832 года и получает временный бафф от десерта.", "key_points": ["Квест фокусируется на секретном рецепте и бренде Hotel Sacher.", "Этапы включают заказ десерта, дегустацию и сбор исторических заметок.", "Награды дают культурный опыт и бафф на энергию."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reserve_table", "text": "Зарезервировать столик в Café Sacher", "type": "interact", "target": "cafe_sacher", "count": 1, "optional": false}, {"id": "learn_recipe_history", "text": "Пообщаться с персоналом и узнать о происхождении рецепта 1832 года", "type": "interact", "target": "sacher_staff", "count": 1, "optional": false}, {"id": "order_sachertorte", "text": "Заказать оригинальный Sachertorte с обязательным абрикосовым джемом и сливками", "type": "consume", "target": "sachertorte_dessert", "count": 1, "optional": false}, {"id": "compare_demel_recipe", "text": "Сравнить альтернативный рецепт Demel через исторические заметки", "type": "research", "target": "demel_recipe_notes", "count": 1, "optional": false}, {"id": "complete_tasting", "text": "Завершить дегустацию отзывом и получить сувенирную упаковку", "type": "interact", "target": "tasting_completion", "count": 1, "optional": false}], "rewards": {"experience": 800, "currency": -12, "items": [{"item_id": "sugar_boost_buff", "name": "Бафф «Сахарный подъём»: +15% Energy на 4 часа", "quantity": 1}, {"item_id": "sacher_gourmet_title", "name": "Титул «Захер Гурман»", "quantity": 1}], "reputation": {}, "flags": ["sacher_gourmet_title"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест VIENNA-2029-003 — короткая гастрономическая история. Игрок прибывает в Hotel Sacher, общается с кондитером и\\nдегустирует фирменный торт, исследуя конфликт брендов и секретную рецептуру.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Зарезервировать столик в Café Sacher.\\n2. Пообщаться с персоналом и узнать о происхождении рецепта 1832 года.\\n3. Заказать оригинальный Sachertorte с обязательным абрикосовым джемом и сливками.\\n4. Сравнить альтернативный рецепт Demel через исторические заметки.\\n5. Завершить дегустацию отзывом и получить сувенирную упаковку.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Рецепт торта создан Францем Захером и строго охраняется.\\n- Спор Sacher vs Demel длится десятилетиями и подчёркивает брендовый статус десерта.\\n- Стоимость порции достигает 10 евро, что показывает премиум сегмент.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 800 XP.\\n- Бафф «Сахарный подъём»: +15% Energy на 4 часа.\\n- Валюта: -12 (стоимость десерта и чаевых).\\n- Титул «Захер Гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные бонусы с временными улучшениями характеристик.\\n- Сбор предметов-артефактов (сувенирная упаковка).\\n- Социальные взаимодействия в премиальной локации.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест дополняет кулинарную линию Вены и открывает доступ к гастрономическим мероприятиям других городов Европы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 595, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.1.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлен Issue в начало файла. Исправлена структура items (id → item_id). Структура теперь полностью соответствует архитектуре системы квестов."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики гастрономии и роскошного опыта."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Описан YAML-квест дегустации торта Захер."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

