INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vienna-2029-prater-riesenrad', 'Вена 2020-2029 — Пратер и Riesenrad', 'Игрок посещает Пратер, поднимается на Riesenrad, исследует деревянные кабины и создает медиа-отчёт об истории объекта.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reach_prater_park", "text": "Добраться до парка Пратер и получить экскурсионный бриф у смотрителя", "type": "explore", "target": "prater_park", "count": 1, "optional": false}, {"id": "explore_riesenrad_history", "text": "Исследовать экспозицию об истории колеса и особенностях его деревянных кабин", "type": "investigate", "target": "riesenrad_exhibition", "count": 1, "optional": false}, {"id": "ride_full_rotation", "text": "Совершить полный оборот (около 20 минут), зафиксировать панорамные виды", "type": "perform", "target": "riesenrad_ride", "count": 1, "optional": false}, {"id": "recreate_third_man_scene", "text": "Воссоздать сцену из фильма «Третий человек» и собрать отзывы туристов", "type": "interact", "target": "third_man_recreation", "count": 1, "optional": false}, {"id": "prepare_media_package", "text": "Подготовить фото- и видеопакет для маркетинга туристических миссий", "type": "interact", "target": "media_package_creation", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "currency": -12, "items": [], "reputation": {"vienna_tourism": 15}, "flags": ["riesenrad_conqueror_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vienna-2029-prater-riesenrad", "title": "Вена 2020-2029 — Пратер и Riesenrad", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-01T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vienna", "prater", "riesenrad"], "topics": ["tourism", "cinematic-history"], "related_systems": ["narrative-service", "travel-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/vienna/2020-2029/quest-008-prater-riesenrad.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристическая линия Вены требовала миссии, раскрывающей символичность парка Пратер и колеса Riesenrad.", "goal": "Показать историю аттракциона, привязать его к культовым кинообразам и собрать визуальный материал для туристических маршрутов.", "essence": "Игрок посещает Пратер, поднимается на Riesenrad, исследует деревянные кабины и создает медиа-отчёт об истории объекта.", "key_points": ["Колесо Riesenrad работает с 1897 года и остаётся одним из главных символов Вены.", "Аттракцион появляется в фильме «Третий человек», что усиливает его культурную значимость.", "Медленное вращение позволяет наблюдать панораму города и дополнить фотоархив."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reach_prater_park", "text": "Добраться до парка Пратер и получить экскурсионный бриф у смотрителя", "type": "explore", "target": "prater_park", "count": 1, "optional": false}, {"id": "explore_riesenrad_history", "text": "Исследовать экспозицию об истории колеса и особенностях его деревянных кабин", "type": "investigate", "target": "riesenrad_exhibition", "count": 1, "optional": false}, {"id": "ride_full_rotation", "text": "Совершить полный оборот (около 20 минут), зафиксировать панорамные виды", "type": "perform", "target": "riesenrad_ride", "count": 1, "optional": false}, {"id": "recreate_third_man_scene", "text": "Воссоздать сцену из фильма «Третий человек» и собрать отзывы туристов", "type": "interact", "target": "third_man_recreation", "count": 1, "optional": false}, {"id": "prepare_media_package", "text": "Подготовить фото- и видеопакет для маркетинга туристических миссий", "type": "interact", "target": "media_package_creation", "count": 1, "optional": false}], "rewards": {"experience": 1000, "currency": -12, "items": [], "reputation": {"vienna_tourism": 15}, "flags": ["riesenrad_conqueror_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест направляет игрока в парк Пратер: необходимо изучить историю аттракциона, прокатиться на Riesenrad и собрать материалы\\nдля туристического гида по культовым видам Вены.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до парка Пратер и получить экскурсионный бриф у смотрителя.\\n2. Исследовать экспозицию об истории колеса и особенностях его деревянных кабин.\\n3. Совершить полный оборот (около 20 минут), зафиксировать панорамные виды.\\n4. Воссоздать сцену из фильма «Третий человек» и собрать отзывы туристов.\\n5. Подготовить фото- и видеопакет для маркетинга туристических миссий.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Wiener Riesenrad открыт в 1897 году, высота 64,75 метра. Деревянные кабины и неторопливое вращение создают атмосферу ретро.\\nПратер остаётся старейшим городским парком развлечений в Европе.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 000 XP.\\n- Расходы: -12 еддис.\\n- Атрибут «Эстетика»: +15.\\n- Ачивка «Покоритель Riesenrad».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Туристические осмотры, фото- и видеофиксация, кино-референсы для LiveOps кампаний.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает туристическую ветку Вены с кинематографическими сюжетами и маршрутами городской романтики.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 600, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.1.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлен Issue в начало файла. Структура quest_definition соответствует архитектуре системы квестов (currency, flags, правильная структура rewards)."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики туризма и кинематографической истории."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Пратер и Riesenrad» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

