INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-detroit-red-wings-hockey', 'Детройт 2020-2029 — Red Wings Hockey', 'Игрок переживает матч Detroit Red Wings, знакомится с традициями Hockeytown и получает спортивные награды.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-detroit-red-wings-hockey", "title": "Детройт 2020-2029 — Red Wings Hockey", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-05T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["detroit", "sports", "hockey"], "topics": ["regional-culture", "lifestyle"], "related_systems": ["narrative-service", "live-ops"], "related_documents": [{"id": "canon-quest-detroit-motor-city", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-341", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/341", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/detroit/2020-2029/quest-009-red-wings-hockey.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий «Red Wings Hockey» был в Markdown и не входил в общую базу знаний.", "goal": "Задокументировать культурный квест о хоккейной идентичности Детройта в структуре YAML.", "essence": "Игрок переживает матч Detroit Red Wings, знакомится с традициями Hockeytown и получает спортивные награды.", "key_points": ["Фокус на культурной гордости города, легендах NHL и фанатских традициях.", "Этапы квеста проводят через арену Little Caesars и уникальную осьминожью традицию.", "Награды дают спортивную репутацию и достижение «Фанат Red Wings»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест посвящён посещению матча Detroit Red Wings, легендам Original Six и традиции «Hockeytown». Игрок погружается в\\nатмосферу команды, символы города и живой спорт.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Прибытие на Little Caesars Arena и знакомство с фанатами.\\n2. Просмотр матча и наблюдение за символикой Hockeytown.\\n3. Выполнение фанатских ритуалов, включая бросок осьминога.\\n4. Финальное скандирование и фиксация хоккейной гордости города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные отсылки", "body": "Отмечены 11 кубков Стэнли, традиция осьминога, роль Little Caesars Arena и прозвище Hockeytown. Квест подчёркивает\\nлокальную идентичность и туризм.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "Игрок получает 1500 XP, тратит 80 едди на билеты и расходники, повышает атрибут «Спорт» на 20 очков и открывает\\nдостижение «Фанат Red Wings». Механики включают спортивное событие, фанатские ритуалы и культурное исследование.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 341, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-detroit-red-wings-hockey"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Red Wings Hockey» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE S