INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-houston-humidity-hell', 'Хьюстон 2020-2029 — Влажная жара', 'Игрок переживает экстремальную влажность города, управляет ресурсами и адаптируется к климатическому стрессу.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "exit_july_august_peak", "text": "Выход на улицу в пик июля-августа и первые признаки перегрева", "type": "interact", "target": "summer_heat_experience", "count": 1, "optional": false}, {"id": "manage_resources", "text": "Управление ресурсами — вода, одежда, время под открытым солнцем", "type": "interact", "target": "heat_resource_management", "count": 1, "optional": false}, {"id": "check_body_reactions", "text": "Проверка реакций организма: запотевание очков, затруднённое дыхание, сниженная выносливость", "type": "interact", "target": "body_heat_reactions", "count": 1, "optional": false}, {"id": "find_ac_shelter", "text": "Финальный блок — поиск убежища с кондиционированием и анализ климатической статистики города", "type": "interact", "target": "ac_shelter_climate_analysis", "count": 1, "optional": false}]'::jsonb, '{"xp": 2000, "currency": 0, "items": [], "reputation": {}, "achievements": [], "skill_boosts": {"endurance": 20, "heat_debuff": -40}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-houston-humidity-hell", "title": "Хьюстон 2020-2029 — Влажная жара", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "2.0.0", "last_updated": "2025-11-29T08:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["houston", "climate", "survival"], "topics": ["environmental", "regional-lifestyle"], "related_systems": ["narrative-service", "survival-system"], "related_documents": [{"id": "canon-quest-houston-hurricane-harvey", "relation": "references"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-342", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/342", "relation": "migrated_to", "migrated_at": "2025-11-22T20:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/houston/2020-2029/quest-003-humidity-hell.md", "visibility": "internal", "audience": ["narrative", "live-ops", "concept"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История о климатическом испытании Хьюстона находилась в Markdown и не была подготовлена к автоматизированной обработке.", "goal": "Оформить квест «Влажная жара» в стандартизированный YAML.", "essence": "Игрок переживает экстремальную влажность города, управляет ресурсами и адаптируется к климатическому стрессу.", "key_points": ["Подчёркнуты экстремальные показатели температуры и влажности летом.", "Маршрут демонстрирует влияние климата на повседневную жизнь и необходимость постоянного кондиционирования.", "Награды усиливают выносливость, но добавляют временный дебафф к стамине."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "exit_july_august_peak", "text": "Выход на улицу в пик июля-августа и первые признаки перегрева", "type": "interact", "target": "summer_heat_experience", "count": 1, "optional": false}, {"id": "manage_resources", "text": "Управление ресурсами — вода, одежда, время под открытым солнцем", "type": "interact", "target": "heat_resource_management", "count": 1, "optional": false}, {"id": "check_body_reactions", "text": "Проверка реакций организма: запотевание очков, затруднённое дыхание, сниженная выносливость", "type": "interact", "target": "body_heat_reactions", "count": 1, "optional": false}, {"id": "find_ac_shelter", "text": "Финальный блок — поиск убежища с кондиционированием и анализ климатической статистики города", "type": "interact", "target": "ac_shelter_climate_analysis", "count": 1, "optional": false}], "rewards": {"xp": 2000, "currency": 0, "items": [], "reputation": {}, "achievements": [], "skill_boosts": {"endurance": 20, "heat_debuff": -40}}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест погружает игрока в климатические реалии Хьюстона. Нужно выжить в условиях +38°C и влажности 90%, управляя состоянием и\\nпланируя действия между кондиционированными зонами.\\n", "mechanics_links": [], "assets": []}, {"id": "survival_path", "title": "Этапы испытания", "body": "1. Выход на улицу в пик июля-августа и первые признаки перегрева.\\n2. Управление ресурсами — вода, одежда, время под открытым солнцем.\\n3. Проверка реакций организма: запотевание очков, затруднённое дыхание, сниженная выносливость.\\n4. Финальный блок — поиск убежища с кондиционированием и анализ климатической статистики города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указаны показатели влажности 90%+, ощущаемая температура до +45°C и повсеместное использование кондиционеров. Раскрыты\\nповседневные привычки жителей, адаптирующихся к климату.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards_mechanics", "title": "Награды и механики", "body": "XP: 2000, Валюта: 0, Параметр «Выносливость»: +20. Временный дебафф «Перегрев» снижает стамину на 40% при высокой влажности.\\nКлючевые механики — климатическое выживание, управление ресурсами и адаптация к экстремальным условиям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 342, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-houston-humidity-hell"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Влажная жара» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

