INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-las-vegas-mob-museum', 'Лас-Вегас 2020-2029 — Музей мафии', 'Игрок проходит экспозиции, узнаёт о Bugsy Siegel, схемах отмывания и трансформации города из-под контроля мафии.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_museum", "text": "Посетить Mob Museum и изучить экспозиции", "type": "travel", "target": "mob_museum", "count": 1, "optional": false}, {"id": "learn_mafia_history", "text": "Узнать о Bugsy Siegel, схемах отмывания и трансформации города", "type": "interact", "target": "mafia_exhibits", "count": 1, "optional": false}, {"id": "visit_speakeasy", "text": "Посетить подпольный бар и архивные экспонаты", "type": "interact", "target": "speakeasy_bar", "count": 1, "optional": true}]'::jsonb, '{"experience": 1800, "money": -75, "items": [{"item_id": "achievement_mafia_expert", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-las-vegas-mob-museum", "title": "Лас-Вегас 2020-2029 — Музей мафии", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:30:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-05T00:00:00+00:00", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["las-vegas", "mafia", "history"], "topics": ["crime-history", "cultural-heritage"], "related_systems": ["narrative-service", "investigation-systems"], "related_documents": [{"id": "canon-quest-las-vegas-neon-boneyard", "relation": "complements"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-347", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/347", "relation": "migrated_to", "migrated_at": "2025-11-22T20:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/las-vegas/2020-2029/quest-007-mob-museum.md", "visibility": "internal", "audience": ["narrative", "content", "vision"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание Mob Museum оставалось в Markdown и не участвовало в формировании криминальных арок.", "goal": "Задокументировать вклад мафии в создание Вегаса и интегрировать музей в нарративный лор.", "essence": "Игрок проходит экспозиции, узнаёт о Bugsy Siegel, схемах отмывания и трансформации города из-под контроля мафии.", "key_points": ["Музей раскрывает эпоху 1940–1980-х, когда мафия управляла казино.", "Подпольный бар и архивные экспонаты усиливают атмосферу запретных времён.", "Награды повышают исторический атрибут и дают достижение «Знаток Мафии»."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_museum", "text": "Посетить Mob Museum и изучить экспозиции", "type": "travel", "target": "mob_museum", "count": 1, "optional": false}, {"id": "learn_mafia_history", "text": "Узнать о Bugsy Siegel, схемах отмывания и трансформации города", "type": "interact", "target": "mafia_exhibits", "count": 1, "optional": false}, {"id": "visit_speakeasy", "text": "Посетить подпольный бар и архивные экспонаты", "type": "interact", "target": "speakeasy_bar", "count": 1, "optional": true}], "rewards": {"experience": 1800, "money": -75, "items": [{"item_id": "achievement_mafia_expert", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест переносит игрока в Mob Museum, где собрана история организованной преступности и её влияния на Лас-Вегас.\\nЭкспозиции показывают, как криминальные синдикаты сформировали индустрию казино.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Осмотр экспозиций о Bugsy Siegel и запуске Flamingo в 1946 году.\\n2. Изучение схем skim, коррупции и судебных процессов против мафии.\\n3. Посещение подземного бара эпохи «сухого закона» и интерактивных стендов.\\n4. Финальное обсуждение: трансформация города под управлением корпораций.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Документирует роль мафии в развитии Лас-Вегаса, ключевые фигуры (Bugsy Siegel), контроль казино до 1980-х и вытеснение\\nорганизованной преступности корпорациями. Указаны культурные связи с Downtown.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "XP: 2500, Валюта: -25, Атрибут «История»: +25, достижение «Знаток Мафии». Механики — историческая реконструкция,\\nобразовательный контент и криминальное наследие.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 50, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 50. Детализирован контент согласно архитектуре квестов."}, {"version": "1.0.0", "date": "2025-11-05", "author": "ai_brain_manager", "changes": "Квест «Музей мафии» перенесён в YAML и расширен структурированным описанием."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min 