INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2030-2039-nefertiti-heist', 'Берлин 2030-2039 — Кража Нефертити', 'Команда выполняет заказ Египта, крадёт бюст Нефертити и решает его дальнейшую судьбу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2030-2039-nefertiti-heist", "title": "Берлин 2030-2039 — Кража Нефертити", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "heist", "restitution"], "topics": ["stealth", "moral-choice"], "related_systems": ["narrative-service", "stealth-system", "economy-service"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}, {"id": "canon-quest-london-2030-2039-british-museum-heist", "relation": "parallels"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2030-2039/quest-015-nefertiti-heist.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "План ограбления бюста Нефертити был описан текстом и не раскрывал стелс, дилеммы и последствия.", "goal": "Зафиксировать сложное ограбление Neues Museum с моральными выборами и международной реакцией.", "essence": "Команда выполняет заказ Египта, крадёт бюст Нефертити и решает его дальнейшую судьбу.", "key_points": ["Контракт поднимает проблему реституции колониальных артефактов.", "Операция требует стелса, подмены бюста и бегства из Германии.", "Финальные решения меняют отношение Египта, Берлина и чёрного рынка."]}, "content": {"sections": [{"id": "planning", "title": "Планирование ограбления", "body": "- Получить контракт от египетского правительства и собрать команду (хакер, арт-эксперт, курьер).\\n- Изучить охранные протоколы Neues Museum и расписание смен.\\n- Подготовить 3D-печатную копию бюста и инструменты отвлечения.\\n", "mechanics_links": ["mechanics/quests/heist-setup.yaml", "mechanics/world/security-systems.yaml"], "assets": []}, {"id": "infiltration", "title": "Проникновение и подмена", "body": "- Стелс-проникновение: отключение датчиков, взлом камер и подавление лазерных сеток.\\n- Подмена бюста Нефертити на копию с точностью до микроскопических дефектов.\\n- Необходжимость скрыть следы, включая контроль веса витрины и температурные сенсоры.\\n", "mechanics_links": ["mechanics/quests/stealth-system.yaml", "mechanics/world/security-systems.yaml"], "assets": []}, {"id": "resolution", "title": "Исходы и последствия", "body": "- Вернуть Египту: получить поддержку Каира и врагов в Берлине.\\n- Продать на чёрном рынке: прибыль 500 000 000 едди, международный розыск.\\n- Оставить Берлину: нарушить контракт, но сохранить местную репутацию.\\n- Любой выбор порождает глобальные новости и модификаторы дипломатии.\\n", "mechanics_links": ["mechanics/quests/branching-outcomes.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 10 000 XP.\\n- Валюта: 0–500 000 000 едди в зависимости от выбора.\\n- Большие изменения репутации (Египет, Германия, чёрный рынок).\\n- Возможность разблокировать «Restitution Arc» в других городах.\\n", "mechanics_links": ["mechanics/economy/rewards.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 54, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-berlin-2030-2039-nefertiti-heist"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Уточнены стелс- и ветвления, добавлены ссылки на механики, обновлены версия и дата."}, {"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован квест «Кража Нефертити» в YAML с проработкой выбора и последствий."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

