INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-amsterdam-2020-2029-canal-cruise', 'Амстердам 2020-2029 — Круиз по каналам', 'Прогулка по историческим каналам Амстердама раскрывает градостроительные особенности XVII века и атмосферу «Северной Венеции».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-amsterdam-2020-2029-canal-cruise", "title": "Амстердам 2020-2029 — Круиз по каналам", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["europe", "amsterdam", "quest"], "topics": ["timeline-author", "urban-exploration"], "related_systems": ["narrative-service", "world-state"], "related_documents": [{"id": "canon-region-europe-amsterdam-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-001-canal-cruise.md", "visibility": "internal", "audience": ["lore", "narrative", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест был описан в Markdown и не был привязан к системам канонических событий Амстердама.", "goal": "Структурировать «Круиз по каналам» в формате YAML, сохранив ключевые этапы маршрута и культурные маркеры.", "essence": "Прогулка по историческим каналам Амстердама раскрывает градостроительные особенности XVII века и атмосферу «Северной Венеции».", "key_points": ["Каналы Herengracht, Prinsengracht и Keizersgracht формируют основной маршрут круиза.", "Узкие дома и плавучие дома подчёркивают архитектурный налог и гибридную жизнь на воде.", "Квест усиливает туристические и атмосферные механики для социального контента."]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Амстердам позиционируется как «Северная Венеция», а система каналов и мостов занесена в наследие UNESCO.\\nКвест предлагает мягкое знакомство с городом через водный маршрут, подчёркивая локальные особенности плотной застройки и перемещения на лодках.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-steps", "title": "Этапы", "body": "1. Арендовать лодку или присоединиться к круизному туру.\\n2. Пройти по Herengracht, Prinsengracht и Keizersgracht, отмечая различия кварталов.\\n3. Изучить узкие дома XVII века и плавучие конструкции.\\n4. Зафиксировать визуальные особенности мостов и велосипедной инфраструктуры.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Опыт: 800 XP.\\nВалюта: −25 (оплата аренды или круиза).\\nЭстетика: +15 к визуальному восприятию города.\\nАчивка: «Капитан Каналов».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Туристический геймплей с акцентом на исследование каналов, архитектурное наблюдение и атмосферу водного города.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Связи и отсылки", "body": "Связан с архитектурной линейкой Амстердама и региональными сюжетами о городской логистике.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 55, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Круиз по каналам» в YAML и структурирование этапов и наград."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

