INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-amsterdam-2020-2029-van-gogh-museum', 'Амстердам 2020-2029 — Музей Ван Гога', 'Игрок проводит несколько часов в музее, изучая «Подсолнухи», «Спальню в Арле» и трагическую судьбу художника.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-amsterdam-2020-2029-van-gogh-museum", "title": "Амстердам 2020-2029 — Музей Ван Гога", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["europe", "amsterdam", "quest"], "topics": ["timeline-author", "cultural-tour"], "related_systems": ["narrative-service", "world-state"], "related_documents": [{"id": "canon-region-europe-amsterdam-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/amsterdam/2020-2029/quest-008-van-gogh-museum.md", "visibility": "internal", "audience": ["lore", "narrative", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о музее Ван Гога оставался в Markdown и не описывал маршрут по ключевым экспонатам.", "goal": "Структурировать посещение музея в YAML, выделив знаковые картины и письма к Тео.", "essence": "Игрок проводит несколько часов в музее, изучая «Подсолнухи», «Спальню в Арле» и трагическую судьбу художника.", "key_points": ["Музей содержит крупнейшую коллекцию картин Ван Гога, включая «Подсолнухи» и «Едоков картофеля».", "Письма к Тео и хроника болезней раскрывают эмоциональную составляющую экспозиции.", "Квест усиливает культурную ветку Амстердама и образовательные сценарии."]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Музей Ван Гога на площади Музеумплейн хранит более 200 произведений художника и архив писем.\\nКвест подчёркивает значение экспозиции для понимания постимпрессионизма и личной истории Винсента.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-steps", "title": "Этапы", "body": "1. Войти в музей и получить аудиогид или сопровождение куратора.\\n2. Изучить зал с «Подсолнухами», «Спальней в Арле» и «Едоками картофеля».\\n3. Посмотреть серию автопортретов и картин периода Арля и Сен-Реми.\\n4. Ознакомиться с письмами к брату Тео и документами о состоянии здоровья.\\n5. Завершить визит в магазине музея, выбрав репродукции или каталог выставки.\\n", "mechanics_links": [], "assets": []}, {"id": "regional-details", "title": "Региональные детали", "body": "Описываются яркие палитры Ван Гога, влияние его болезни и недооценённость при жизни.\\nПодчеркивается роль музея в сохранении наследия и туристической привлекательности Амстердама.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Опыт: 1500 XP.\\nВалюта: −20.\\nКультура: +20.\\nАчивка: «Ценитель Ван Гога».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Культурные и образовательные механики демонстрируют влияние искусства на эмоциональные выборы игрока.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 55, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Музей Ван Гога» в YAML и детализация основного маршрута."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

