INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-atlanta-2029-008-georgia-aquarium', 'Атланта: Georgia Aquarium', 'Игрок исследует океанические экспозиции, взаимодействует с биологами и принимает решение о развитии природных программ Атланты.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-atlanta-2029-008-georgia-aquarium", "title": "Атланта: Georgia Aquarium", "document_type": "canon", "category": "city-quest", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:16:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["atlanta", "aquarium", "marine-life"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "progression-service"], "related_documents": [{"id": "github-issue-297", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/297", "relation": "migrated_to", "migrated_at": "2025-11-22T19:16:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-008-aquarium.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик про Georgia Aquarium не содержал сценарных веток и интеграций с системами.", "goal": "Структурировать посещение крупнейшего аквариума с учётом образовательных и туристических бонусов.", "essence": "Игрок исследует океанические экспозиции, взаимодействует с биологами и принимает решение о развитии природных программ Атланты.", "key_points": ["Подчёркнут масштаб 38 млн литров и уникальные китовые акулы.", "Игрок выбирает между образовательным туром и коммерческим мероприятием.", "Прогрессия даёт бонусы к природной линии города."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ATLANTA-2029-008`\\n- Формат: туристическое событие, сложность низкая, solo, длительность 2–4 часа.\\n- Локации: Ocean Voyager, Dolphin Coast, Cold Water Quest.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "exhibits", "title": "Основные экспозиции", "body": "1. Ocean Voyager: подводный туннель, наблюдение за китовыми акулами.\\n2. Dolphin Coast: посещение шоу и взаимодействие с тренерами.\\n3. Cold Water Quest: пингвины, белухи, каланы.\\n4. Экспо об основателе Bernie Marcus и финансировании аквариума.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "choices", "title": "Решения игрока", "body": "- Поддержать образовательную программу и улучшить репутацию среди семейных NPC.\\n- Организовать ночной коммерческий ивент для увеличения дохода.\\n- Финансировать программу спасения морских животных, влияя на progression-service.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 1500 XP, расходы 45 кредитов на билеты и сувениры.\\n- Показатель «Природа» +20, ачивка «Хранитель океана».\\n- Бафф «Marine Inspiration» (+10% к природным исследованиям на 6 часов).\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 297, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста о Georgia Aquarium в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.le