INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-venice-beach-skate', 'Лос-Анджелес — Скейт на Venice Beach', 'Игрок участвует в культовом скейт-турнире Лос-Анджелеса, демонстрируя трюки и зарабатывая уважение сцены.', 'side', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "register_tournament", "text": "Добраться до Venice Beach и зарегистрироваться на турнир", "type": "travel", "target": "venice_beach_skatepark", "count": 1, "optional": false}, {"id": "practice_tricks", "text": "Освоить чаши, рампы и перила скейтпарка, разогревая толпу", "type": "minigame", "target": "skatepark_practice", "count": 1, "optional": false}, {"id": "perform_tricks", "text": "Выполнить набор трюков в рамках сетки турнира", "type": "minigame", "target": "tournament_rounds", "count": 3, "optional": false}, {"id": "wait_judging", "text": "Дождаться решений судей по технике, стилю и креативности", "type": "dialogue", "target": "tournament_judges", "count": 1, "optional": false}, {"id": "final_round", "text": "Участвовать в финальном заезде и церемонии награждения", "type": "minigame", "target": "final_round", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": 20000, "reputation": {"venice_beach_skate": 20}, "items": ["legendary_skateboard"], "unlocks": {"achievements": ["Скейт-чемпион Venice Beach"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-venice-beach-skate", "title": "Лос-Анджелес — Скейт на Venice Beach", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:40:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T14:30:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["los-angeles", "skate-culture", "events"], "topics": ["regional-experiences", "sports"], "related_systems": ["event-service", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-003-venice-beach-skate.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Региональные квесты Лос-Анджелеса были оформлены в Markdown и не участвовали в проверке знаний.", "goal": "Структурировать событие скейт-турнира на Venice Beach в YAML с ключевыми этапами и наградами.", "essence": "Игрок участвует в культовом скейт-турнире Лос-Анджелеса, демонстрируя трюки и зарабатывая уважение сцены.", "key_points": ["Сцена расположена на Venice Beach Skatepark с акцентом на уличную культуру ЛА.", "Турнир оценивает технику, стиль и креативность в серии трюков.", "Победитель получает легендарный скейтборд и крупную денежную награду, участники — опыт."]}, "quest_definition": {"quest_type": "side", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "register_tournament", "text": "Добраться до Venice Beach и зарегистрироваться на турнир", "type": "travel", "target": "venice_beach_skatepark", "count": 1, "optional": false}, {"id": "practice_tricks", "text": "Освоить чаши, рампы и перила скейтпарка, разогревая толпу", "type": "minigame", "target": "skatepark_practice", "count": 1, "optional": false}, {"id": "perform_tricks", "text": "Выполнить набор трюков в рамках сетки турнира", "type": "minigame", "target": "tournament_rounds", "count": 3, "optional": false}, {"id": "wait_judging", "text": "Дождаться решений судей по технике, стилю и креативности", "type": "dialogue", "target": "tournament_judges", "count": 1, "optional": false}, {"id": "final_round", "text": "Участвовать в финальном заезде и церемонии награждения", "type": "minigame", "target": "final_round", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": 20000, "reputation": {"venice_beach_skate": 20}, "items": ["legendary_skateboard"], "unlocks": {"achievements": ["Скейт-чемпион Venice Beach"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор", "body": "Venice Beach Skatepark — сердце калифорнийской скейт-культуры. Игрок прибывает на набережную, где проходят локальные турниры,\\nсочетающие музыку, уличных артистов и дух соревновательной свободы.\\n", "mechanics_links": [], "assets": []}, {"id": "flow", "title": "Ход события", "body": "1. Добраться до Venice Beach и зарегистрироваться.\\n2. Освоить чаши, рампы и перила скейтпарка, разогревая толпу.\\n3. Выполнить набор трюков в рамках сетки турнира.\\n4. Дождаться решений судей по технике, стилю и креативности.\\n5. Участвовать в финальном заезде и церемонии награждения.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Граффити на бетонных чашах и барьерках.\\n- Bodybuilders на Muscle Beach создают контраст культур.\\n- Street performers и продавцы на boardwalk формируют шумовой фон.\\n- Теплый океанский бриз и запах еды вокруг.\\n- Локальные skate punks делятся лайфхаками.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Победа: 20,000 €$ и легендарный скейтборд.\\nУчастие: 1,500 опытов и репутация в субкультуре Venice Beach.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Мини-игра со скейт-трюками и системой комбо.\\n- Турнирная сетка с судейством NPC.\\n- Тематический модификатор «Калифорнийская культура» для диалогов и заработка репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "links", "title": "Связи", "body": "Поддерживает линию «Скейт-культура Лос-Анджелеса» и пляжные активности West Coast.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 1125, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Оформлен квест Venice Beach Skate в структуре знаний."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

