INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-las-vegas-casino-gambling', 'Лас-Вегас 2020-2029 — Казино и азартные игры', 'Игрок испытывает рулетку, блэкджек, слоты и покер, сталкиваясь с бесплатными напитками, отсутствием часов и дилеммой — уйти вовремя или проиграть всё.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_casino", "text": "Войти в казино уровня Bellagio или Caesars", "type": "travel", "target": "casino_entrance", "count": 1, "optional": false}, {"id": "play_slots", "text": "Попробовать слоты", "type": "interact", "target": "slot_machines", "count": 1, "optional": false}, {"id": "play_blackjack", "text": "Сыграть в блэкджек", "type": "interact", "target": "blackjack_table", "count": 1, "optional": false}, {"id": "play_roulette", "text": "Попробовать рулетку", "type": "interact", "target": "roulette_table", "count": 1, "optional": false}, {"id": "play_poker", "text": "Сыграть в покер", "type": "interact", "target": "poker_table", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": -500, "items": [{"item_id": "achievement_vegas_gambler", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-las-vegas-casino-gambling", "title": "Лас-Вегас 2020-2029 — Казино и азартные игры", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:30:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-05T00:00:00+00:00", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["las-vegas", "gambling", "risk"], "topics": ["hospitality", "risk-management"], "related_systems": ["narrative-service", "economy-simulation"], "related_documents": [{"id": "canon-quest-las-vegas-strip", "relation": "references"}, {"id": "canon-quest-las-vegas-bellagio-fountains", "relation": "complements"}, {"id": "github-issue-345", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/345", "relation": "migrated_to", "migrated_at": "2025-11-22T20:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/las-vegas/2020-2029/quest-002-casino-gambling.md", "visibility": "internal", "audience": ["narrative", "economy", "vision"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест казино существовал как Markdown и не входил в конвейер задач Vision.", "goal": "Описать механики азартных игр, экономические риски и атмосферу Las Vegas casinos в структурированном формате.", "essence": "Игрок испытывает рулетку, блэкджек, слоты и покер, сталкиваясь с бесплатными напитками, отсутствием часов и дилеммой — уйти вовремя или проиграть всё.", "key_points": ["Зафиксированы ключевые казино, бесплатные напитки и бессрочность игровых залов.", {"Введён риск случайного изменения валюты": "от -500 до +5000."}, "Игрок получает достижение «Игрок Вегаса» и исследует психологию «house always wins»."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_casino", "text": "Войти в казино уровня Bellagio или Caesars", "type": "travel", "target": "casino_entrance", "count": 1, "optional": false}, {"id": "play_slots", "text": "Попробовать слоты", "type": "interact", "target": "slot_machines", "count": 1, "optional": false}, {"id": "play_blackjack", "text": "Сыграть в блэкджек", "type": "interact", "target": "blackjack_table", "count": 1, "optional": false}, {"id": "play_roulette", "text": "Попробовать рулетку", "type": "interact", "target": "roulette_table", "count": 1, "optional": false}, {"id": "play_poker", "text": "Сыграть в покер", "type": "interact", "target": "poker_table", "count": 1, "optional": false}], "rewards": {"experience": 2000, "money": -500, "items": [{"item_id": "achievement_vegas_gambler", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест знакомит с сердцем Лас-Вегаса — азартными играми. Игрок посещает иконические казино, пробует разные виды игр\\nи наблюдает, как индустрия удерживает гостей внутри.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay_flow", "title": "Этапы", "body": "1. Вход в казино уровня Bellagio или Caesars.\\n2. Последовательное испытание слотов, блэкджека, рулетки и покера.\\n3. Фиксация бесплатных коктейлей и отсутствия часов.\\n4. Анализ стратегии «уйти вовремя» против соблазна продолжать.\\n5. Итоговый выбор определяет репутацию и последствия.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Документ отмечает, что «house always wins», казино убирают часы и стимулируют игроков бесплатным алкоголем.\\nЭти элементы усиливают риск зависимости и подчеркивают экономическую модель города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "XP: 2000, Валюта: -500 до +5000 (случайный диапазон), риск потерять всё и достижение «Игрок Вегаса».\\nМеханики — азартные игры, риск-менеджмент и поведенческие ловушки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 345, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 50. Детализирован контент согласно архитектуре квестов."}, {"version": "1.0.0", "date": "2025-11-05", "author": "ai_brain_manager", "changes": "Квест азартных игр структурирован в YAML с фиксацией рисков и наград."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

