-- Quest: canon-quest-la-santa-monica-pier
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-santa-monica-pier', 'Лос-Анджелес — Пирс Санта-Моники', 'Игрок проживает классический день на пирсе Санта-Моники: аттракционы, аркады, рыбалка и закат над океаном.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-santa-monica-pier", "title": "Лос-Анджелес — Пирс Санта-Моники", "document_type": "canon", "category": "lore-quests", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T14:32:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T14:32:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["los-angeles", "santa-monica", "tourism"], "topics": ["regional-experiences", "relaxation"], "related_systems": ["event-service", "wellbeing-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-004-santa-monica-pier.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание социального события Santa Monica Pier оставалось в Markdown и не было включено в канон.", "goal": "Оцифровать квест отдыха на пирсе в YAML с этапами, наградами и атмосферой Route 66.", "essence": "Игрок проживает классический день на пирсе Санта-Моники: аттракционы, аркады, рыбалка и закат над океаном.", "key_points": ["Santa Monica Pier выступает знаковым финалом Route 66 и культурным хабом Лос-Анджелеса.", "Механики включают мини-игры в аркадах, расслабление персонажа и наблюдение за океаном.", "Квест уменьшает стресс и выдаёт ачивку «Закат Санта-Моники»."]}, "content": {"sections": [{"id": "overview", "title": "Обзор", "body": "Пирс Санта-Моники — историческое место с 1909 года, сочетающее парк Pacific Park, аркады и виды на океан.\\nКвест предлагает мягкую туристическую активность с акцентом на атмосферу и региональные отсылки.\\n", "mechanics_links": [], "assets": []}, {"id": "flow", "title": "Ход события", "body": "1. Прогуляться по пирсу, погружаясь в шум толпы и музыку уличных артистов.\\n2. Прокатиться на Ferris Wheel с видом на закат.\\n3. Сразиться в аркадных играх, тратя жетоны и собирая билеты.\\n4. Устроить рыбалку с конца пирса, наблюдая за серферами.\\n5. Завершить сценарий просмотром заката над Тихим океаном.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Неоновые огни колеса обозрения формируют узнаваемый skyline.\\n- Классические аркады поддерживают ретро-эстетику Route 66.\\n- Океан шумит под настилом, создавая расслабляющий фон.\\n- Серферы и рыбаки добавляют живости сцене.\\n- Знак «End of the Trail» подчёркивает завершение легендарного маршрута.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 600 опыта.\\n- Потрачено 50 €$ на развлечения.\\n- Снижение стресса на 40%.\\n- Ачивка «Закат Санта-Моники».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Туристический сценарий с модификатором настроения.\\n- Мини-игры: аркады и рыбалка.\\n- Атмосферные события пляжной жизни Калифорнии.\\n", "mechanics_links": [], "assets": []}, {"id": "links", "title": "Связи", "body": "Поддерживает пляжную сюжетную линию Лос-Анджелеса и сет путешествий по Западному побережью.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 49, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Оформлен квест Santa Monica Pier в формате YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

