INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-taco-truck', 'Лос-Анджелес — Taco Truck', 'Игрок охотится за лучшими tacos al pastor, общается с владельцем и получает гастрономический бафф.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-taco-truck", "title": "Лос-Анджелес — Taco Truck", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T12:20:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:20:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["los_angeles", "street_food", "culture"], "topics": ["regional-content", "culinary"], "related_systems": ["social-system", "buff-system"], "related_documents": [{"id": "canon-quest-la-hollywood-sign", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-008-taco-truck.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно отразить повседневную культуру Лос-Анджелеса через уличную мексиканскую кухню.", "goal": "Создать короткий социальный квест о поиске легендарного taco truck и знакомстве с владельцем.", "essence": "Игрок охотится за лучшими tacos al pastor, общается с владельцем и получает гастрономический бафф.", "key_points": ["L.A. street food с насыщенной латиноамериканской идентичностью.", "Диалог с владельцем раскрывает миграционную историю.", "Награда даёт временный бонус к здоровью и достижение «Тако-Гурман»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 30-60 минут.\\nНаграды: 500 XP, -5 единиц валюты, +20% HP на 6 часов, достижение «Тако-Гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать рекомендации о лучшем taco truck в районе.\\n2. Найти яркий грузовик с традиционной раскраской и salsa-баром.\\n3. Заказать набор tacos (al pastor, carne asada, pescado) и оценить вкус.\\n4. Поговорить с владельцем о миграции и семейном бизнесе.\\n5. Получить дисконт и отметку «постоянный клиент».\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Taco trucks — символ лос-анджелесской уличной еды с сильным мексиканским влиянием.\\nМеню включает cilantro, лук, salsa и лайм как базовые топпинги.\\nСтатистика подчёркивает 48% испаноязычного населения города.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест добавляет точку быстрого восстановления здоровья и социальную репутацию в районе.\\nМожет открыть цепочку culinary quests, связанных с латиноамериканскими фестивалями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "L.A. Taco Culture", "link": "https://www.lataco.com/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Создан квест о культовом taco truck Лос-Анджелеса с системными баффами."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.object