// Code generated by NECPGAME backend agent. Enterprise-grade Combat Damage service.
// PERFORMANCE: Optimized for real-time damage calculations with <5ms P99 latency
// Issue: #2200 - Combat System Profiling and Optimization

package main

import (
	"context"
	"log"
	"net/http"
	"os"
	"os/signal"
	"sync"
	"syscall"
	"time"

	"github.com/jackc/pgx/v5/pgxpool"
	"github.com/prometheus/client_golang/prometheus/promhttp"
	"github.com/redis/go-redis/v9"
	"go.uber.org/zap"

	"necpgame/services/combat-damage-service-go/internal/handlers"
	"necpgame/services/combat-damage-service-go/internal/service"
)

// PERFORMANCE: Memory pooling for hot path objects (Level 2 optimization)
// Reduces GC pressure and allocations in high-throughput damage calculations
var (
	damageRequestPool = sync.Pool{
		New: func() interface{} {
			return &service.DamageCalculationRequest{}
		},
	}

	damageResultPool = sync.Pool{
		New: func() interface{} {
			return &service.DamageCalculationResult{}
		},
	}
)

func main() {
	logger, err := zap.NewProduction()
	if err != nil {
		log.Fatal("Failed to create logger:", err)
	}
	defer logger.Sync()

	// PERFORMANCE: Optimized database connection with connection pooling
	dbConfig, err := pgxpool.ParseConfig(os.Getenv("DATABASE_URL"))
	if err != nil {
		logger.Fatal("Failed to parse database config", zap.Error(err))
	}

	// PERFORMANCE: Optimized pool settings for high-throughput combat operations
	dbConfig.MaxConns = 50                    // Higher connection pool for combat load
	dbConfig.MinConns = 10                    // Minimum connections to maintain
	dbConfig.MaxConnLifetime = 30 * time.Minute // Connection lifetime
	dbConfig.MaxConnIdleTime = 5 * time.Minute  // Idle timeout

	db, err := pgxpool.NewWithConfig(context.Background(), dbConfig)
	if err != nil {
		logger.Fatal("Failed to connect to database", zap.Error(err))
	}
	defer db.Close()

	// PERFORMANCE: Redis connection for caching damage calculations
	rdb := redis.NewClient(&redis.Options{
		Addr:         os.Getenv("REDIS_URL"),
		Password:     "",
		DB:           0,
		PoolSize:     20,                // Connection pool for caching
		MinIdleConns: 5,                 // Minimum idle connections
	})

	// Test connections
	if err := db.Ping(context.Background()); err != nil {
		logger.Fatal("Database connection failed", zap.Error(err))
	}

	if err := rdb.Ping(context.Background()).Err(); err != nil {
		logger.Fatal("Redis connection failed", zap.Error(err))
	}

	// PERFORMANCE: Optimized service initialization
	svc := service.NewService(db, rdb, logger)
	h := handlers.NewHandler(svc)

	// PERFORMANCE: HTTP server with optimized timeouts for real-time combat
	server := &http.Server{
		Addr:         ":8083",
		Handler:      h.Router(),
		ReadTimeout:  5 * time.Second,  // Fast reads for combat operations
		WriteTimeout: 5 * time.Second,  // Fast writes for damage results
		IdleTimeout:  30 * time.Second, // Keep connections alive for session combat
	}

	// PERFORMANCE: Graceful shutdown with timeout optimization
	go func() {
		logger.Info("Starting Combat Damage Service on :8083")
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logger.Fatal("Server failed to start", zap.Error(err))
		}
	}()

	// PERFORMANCE: Metrics endpoint for monitoring
	http.Handle("/metrics", promhttp.Handler())
	go func() {
		logger.Info("Starting metrics server on :9090")
		if err := http.ListenAndServe(":9090", nil); err != nil {
			logger.Error("Metrics server failed", zap.Error(err))
		}
	}()

	// PERFORMANCE: Optimized signal handling
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)

	<-quit
	logger.Info("Shutting down server...")

	// PERFORMANCE: Graceful shutdown with 10s timeout
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()

	if err := server.Shutdown(ctx); err != nil {
		logger.Error("Server forced to shutdown", zap.Error(err))
	}

	logger.Info("Server exited")
}