package analytics

import "time"

// CombatAnalytics tracks combat performance metrics
type CombatAnalytics struct {
	SessionID    string
	StartTime    time.Time
	EndTime      time.Time
	DamageDealt  int
	DamageTaken  int
	ActionsCount int
	Victory      bool
}

// RecordAction records a combat action for analytics
func (a *CombatAnalytics) RecordAction(actionType string, damage int) {
	a.ActionsCount++
	if damage > 0 {
		a.DamageDealt += damage
	}
}

// Finalize completes analytics recording
func (a *CombatAnalytics) Finalize(victory bool) {
	a.EndTime = time.Now()
	a.Victory = victory
}